import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function Viewdm() {
  const[khoa ,setData] = useState({});

  const {id_khoa} = useParams();

  useEffect(()=>{
    axios.get(`http://localhost:5000/api/getdm/${id_khoa}`)
    .then((resp) => setData({...resp.data[0]}));
  },[id_khoa]);

  return (
    <div>
      <h3 className="mb-0">Thông tin khoa</h3>
      <hr />
      <div className="row">
        <div className="col mb-3">
          <label className="form-label">Mã khoa</label>
          <input
            type="text"
            name="id_khoa"
            className="form-control"
            placeholder="Mã danh mụ"
            value={ma_danh_muc}
            readonly
          />
        </div>
        <div className="col mb-3">
          <label className="form-label">Tên danh mục</label>
          <input
            type="text"
            name="ten_danh_muc"
            className="form-control"
            placeholder="Tên danh mục"
            value={danhmuc.ten_danh_muc}
            readonly
          />
        </div>
      </div>
    </div>
  );
}
