import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initiaState = {
    ho_ten_bn: "",
    ngay_dat_lich: "",
    tong_tien: "",
    trang_thai: "",
    dia_chi_bn: "",
    ghi_chu: "",
    sdt_bn: "",
    ngay_kham: "",
    id_bac_si: "",
};

const formatCurrency = (number) => {
    return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
};

export default function EditHD() {
    const [state, setState] = useState(initiaState);
    const { ho_ten_bn, ngay_dat_lich, tong_tien, trang_thai, dia_chi_bn, ghi_chu, sdt_bn, ngay_kham, id_bac_si } = state;
    const { id_lich_kham } = useParams();
    const [bacSiData, setBacSiData] = useState({});
    const navigate = useNavigate();

    // Gọi API lấy thông tin bác sĩ khi id_bac_si có giá trị
    useEffect(() => {
        if (id_bac_si) {
            axios.get(`http://localhost:5000/api/getnv/${id_bac_si}`)
                .then((resp) => setBacSiData(resp.data))
                .catch((error) => toast.error("Không thể lấy thông tin bác sĩ"));
        }
    }, [id_bac_si]);

    // Lấy thông tin lịch khám
    useEffect(() => {
        axios.get(`http://localhost:5000/api/getlk/${id_lich_kham}`)
            .then((resp) => setState({ ...resp.data[0] }));
    }, [id_lich_kham]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!trang_thai) {
            toast.error("Vui lòng nhập đầy đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin?")) {
                axios.put(`http://localhost:5000/api/updatehd/${id_lich_kham}`, {
                    trang_thai
                }).then(() => {
                    setState({ trang_thai: "" });
                    toast.success("Sửa hóa đơn thành công!");
                    setTimeout(() => navigate("/Indexhd"), 500);
                }).catch((err) => toast.error(err.response.data));
            }
        }
    };

    return (
        <div>
            <h3 className="mb-0">Cập nhật lịch khám</h3>
            <hr />
            <form onSubmit={handleSubmit} enctype="multipart/form-data">
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Ngày đặt khám</label>
                        <input type="date" name="ngay_dat_lich" className="form-control" onChange={handleInputChange} placeholder="Ngày đặt khám" value={ngay_dat_lich?.slice(0, 10) || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Ngày khám</label>
                        <input type="date" name="ngay_kham" className="form-control" onChange={handleInputChange} placeholder="Ngày đặt khám" value={ngay_kham?.slice(0, 10) || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Tên bệnh nhân</label>
                        <input type="text" name="ho_ten_bn" className="form-control" onChange={handleInputChange} placeholder="Tên bệnh nhân" value={ho_ten_bn || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Số điện thoại</label>
                        <input type="text" name="sdt_bn" className="form-control" onChange={handleInputChange} placeholder="Số điện thoại" value={sdt_bn || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Bác sĩ</label>
                        {/* Hiển thị tên bác sĩ, không cho phép chỉnh sửa */}
                        <input type="text" className="form-control" value={bacSiData.ho_ten || "Đang tải..."} disabled />
                    </div>

                    <div className="col mb-3">
                        <select
                            style={{ marginTop: '31px' }}
                            name="trang_thai"
                            className="form-control"
                            onChange={handleInputChange}
                            value={trang_thai}
                        >
                            <option value="2">Đã Duyệt</option>
                            <option value="1">Chưa Duyệt</option>
                            <option value="3">Đang sắp xếp lịch</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Địa chỉ</label>
                        <input type="text" name="dia_chi_bn" className="form-control" onChange={handleInputChange} placeholder="Địa chỉ" value={dia_chi_bn || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Tổng tiền</label>
                        <input type="text" name="tong_tien" className="form-control" onChange={handleInputChange} placeholder="Tổng tiền" value={formatCurrency(tong_tien) || ""} />
                    </div>
                </div>
                <div className='row'>
                    <div className="col mb-3">
                        <label className="form-label">Mô tả</label>
                        <input type="text" name="ghi_chu" className="form-control" onChange={handleInputChange} placeholder="Ghi chú" value={ghi_chu || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">Cập nhật</button>
                    </div>
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">In Đơn</button>
                    </div>
                </div>
            </form>
        </div>
    )
}
