import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function LoginForm() {
  const [email, setEmail] = useState('');
  const [matKhau, setMatKhau] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        email,
        mat_khau: matKhau,
      });

      // Kiểm tra chính xác phản hồi từ API
      if (response?.data?.success === true) {
        toast.success("Đăng nhập thành công!");
        localStorage.setItem('token', response.data.token);
        navigate('/'); // Điều hướng đến trang chính sau khi đăng nhập
      } else if (response?.data?.success === false) {
        const errorMessage = response?.data?.message || "Đăng nhập thất bại. Vui lòng kiểm tra lại email và mật khẩu.";
        toast.error(errorMessage);
      } else {
        toast.error("Phản hồi không mong đợi từ server. Vui lòng thử lại.");
      }
    } catch (error) {
      toast.error("Có lỗi xảy ra khi đăng nhập. Vui lòng thử lại sau.");
      console.error("Error logging in:", error); // Log lỗi chi tiết trong console để tiện theo dõi
    }
  };

  return (
    <section className="h-100 gradient-form" style={{ backgroundColor: '#eee' }}>
      <div className="container py-5 h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-xl-10">
            <div className="card rounded-3 text-black">
              <div className="row g-0">
                <div className="col-lg-6">
                  <div className="card-body p-md-5 mx-md-4">
                    <div className="text-center">
                      <img
                        src="https://mdbootstrap.com/img/Photos/new-templates/bootstrap-login-form/lotus.webp"
                        style={{ width: '185px' }}
                        alt="logo"
                      />
                      <h4 className="mt-1 mb-5 pb-1">ĐA KHOA PHỐ NỐI</h4>
                    </div>

                    <form onSubmit={handleLogin}>
                      <div className="form-outline mb-4">
                        <input
                          type="email"
                          id="form2Example11"
                          className="form-control"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                          required
                        />
                        <label className="form-label" htmlFor="form2Example11">
                          Email/SĐT
                        </label>
                      </div>

                      <div className="form-outline mb-4">
                        <input
                          type="password"
                          id="form2Example22"
                          className="form-control"
                          value={matKhau}
                          onChange={(e) => setMatKhau(e.target.value)}
                          required
                        />
                        <label className="form-label" htmlFor="form2Example22">
                          Mật Khẩu
                        </label>
                      </div>

                      <div className="text-center pt-1 mb-5 pb-1">
                        <button
                          className="btn btn-primary1 btn-block fa-lg gradient-custom-2 mb-3"
                          type="submit"
                        >
                          Đăng Nhập
                        </button>
                        <a className="text-muted" href="#!">
                          Bạn quên mật khẩu?
                        </a>
                      </div>

                      <div className="d-flex align-items-center justify-content-center pb-4">
                        <p className="mb-0 me-2">Bạn chưa có tài khoản?</p>
                        <button type="button" className="btn btn-outline-danger">
                          Đăng ký
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
                <div className="col-lg-6 d-flex align-items-center gradient-custom-2">
                  <img src="" alt="" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default LoginForm;
