import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initiaState = {
    ho_ten_bn: "",
    ngay_dat_lich: "",
    tong_tien: "",
    trang_thai: "",
    dia_chi_bn: "",
    ghi_chu: "",
    sdt_bn: "",
    ngay_kham: "",
    id_bac_si: "",
    id_phong_kham: "",
};

export default function EditLichKhamBenh() {
    const [state, setState] = useState(initiaState);
    const [khoaList, setKhoaList] = useState([]);
    const [bacSiList, setBacSiList] = useState([]);
    const { ho_ten_bn, ngay_dat_lich, trang_thai, dia_chi_bn, ghi_chu, sdt_bn, ngay_kham, id_phong_kham, id_bac_si, chuyen_khoa } = state;
    const { id_lich_kham } = useParams();
    const navigate = useNavigate();

    // Lấy thông tin lịch khám
    useEffect(() => {
        axios.get(`http://localhost:5000/api/getlk/${id_lich_kham}`)
            .then((resp) => {
                setState({ ...resp.data[0] });
            })
            .catch((error) => toast.error("Không thể lấy thông tin lịch khám"));
    }, [id_lich_kham]);

    // Lấy danh sách khoa và bác sĩ
    useEffect(() => {
        axios.get('http://localhost:5000/api/getalldmkhoa')
            .then((resp) => {
                setKhoaList(resp.data);
            })
            .catch((error) => toast.error("Không thể lấy danh sách khoa"));

        // Lấy danh sách bác sĩ theo chuyen_khoa
        if (state.chuyen_khoa) {
            fetchDoctorsByKhoa(state.chuyen_khoa);
        }
    }, [state.chuyen_khoa]);

    // Hàm lấy bác sĩ theo id_khoa
    const fetchDoctorsByKhoa = (id_khoa) => {
        axios.get(`http://localhost:5000/api/getdoctors/${id_khoa}`)
            .then((resp) => {
                setBacSiList(resp.data);
            })
            .catch((error) => toast.error("Không thể lấy danh sách bác sĩ"));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!trang_thai) {
            toast.error("Vui lòng nhập đầy đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin?")) {
                axios.put(`http://localhost:5000/api/updatelk/${id_lich_kham}`, { trang_thai })
                    .then(() => {
                        toast.success("Cập nhật lịch khám thành công!");
                        setTimeout(() => navigate("/Indexlichkham"), 500);
                    })
                    .catch((err) => toast.error(err.response.data));
            }
        }
    }

    return (
        <div>
            <h3 className="mb-0">Cập nhật lịch khám</h3>
            <hr />
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Chuyên khoa khám bệnh</label>
                        <select
                            name="chuyen_khoa"
                            className="form-control"
                            onChange={(e) => {
                                handleInputChange(e);
                                fetchDoctorsByKhoa(e.target.value);
                            }}
                            value={chuyen_khoa}
                        >
                            {khoaList.map((khoa) => (
                                <option key={khoa.id_khoa} value={khoa.id_khoa}>
                                    {khoa.ten_khoa}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Bác sĩ</label>
                        <select
                            name="id_bac_si"
                            className="form-control"
                            onChange={handleInputChange}
                            value={id_bac_si}
                        >
                            {bacSiList.map((bacsi) => (
                                <option key={bacsi.id_bac_si} value={bacsi.id_bac_si}>
                                    {bacsi.ten_bac_si}
                                </option>
                            ))}
                        </select>
                    </div>
                </div>
                {/* Các trường khác */}
                <div className="d-grid">
                    <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">Cập nhật</button>
                </div>
            </form>
        </div>
    );
}
