import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initialState = {
    ten_thuoc: "",
    id_loai_thuoc: "",
    so_luong: "",
    don_vi: "",
    gia: "",
    ngay_nhap: "",
    hinh_anh: "",
};

export default function EditThuocVatTu() {
    const [state, setState] = useState(initialState);
    const [file, setFile] = useState(null);
    const { ten_thuoc, id_loai_thuoc, so_luong, don_vi, gia, ngay_nhap, hinh_anh } = state;
    const { id_thuoc_vat_tu } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:5000/api/getTVTid/${id_thuoc_vat_tu}`)
            .then((resp) => setState({ ...resp.data[0] }))
            .catch((err) => toast.error(err.response.data));
    }, [id_thuoc_vat_tu]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!ten_thuoc || !id_loai_thuoc || !so_luong || !don_vi || !gia || !ngay_nhap) {
            toast.error("Vui lòng nhập đầy đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin thuốc/vật tư?")) {
                const updatedData = {
                    ten_thuoc,
                    id_loai_thuoc,
                    so_luong,
                    don_vi,
                    gia,
                    ngay_nhap,
                    hinh_anh: file ? file.name : hinh_anh // Giữ nguyên hình ảnh cũ nếu không có file mới
                };

                axios.put(`http://localhost:5000/api/updateTVT/${id_thuoc_vat_tu}`, updatedData)
                    .then(() => {
                        setState(initialState);
                        toast.success("Cập nhật thông tin thuốc/vật tư thành công!");
                        setTimeout(() => navigate("/IndexTVT"), 500);
                    })
                    .catch((err) => toast.error(err.response.data));
            }
        }
    };

    return (
        <div>
            <h3 className="mb-0">Cập nhật thuốc/vật tư</h3>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Tên thuốc</label>
                        <input type="text" name="ten_thuoc" className="form-control" onChange={handleInputChange} placeholder="Tên thuốc" value={ten_thuoc || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Loại thuốc</label>
                        <input type="text" name="id_loai_thuoc" className="form-control" onChange={handleInputChange} placeholder="Loại thuốc" value={id_loai_thuoc || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Số lượng</label>
                        <input type="number" name="so_luong" className="form-control" onChange={handleInputChange} placeholder="Số lượng" value={so_luong || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Đơn vị</label>
                        <input type="text" name="don_vi" className="form-control" onChange={handleInputChange} placeholder="Đơn vị" value={don_vi || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Giá</label>
                        <input type="text" name="gia" className="form-control" onChange={handleInputChange} placeholder="Giá" value={gia || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Ngày nhập</label>
                        <input type="date" name="ngay_nhap" className="form-control" onChange={handleInputChange} value={ngay_nhap || ""} />
                    </div>
                </div>
                <div className="row">
                    <img style={{ borderRadius: '10px', marginLeft: '10px' }} src={hinh_anh} width='150' height='180' className="img img-responsive" alt={ten_thuoc} />
                    <div className="col mb-3">
                        <label className="form-label">Hình ảnh</label>
                        <input type="file" name="hinh_anh" className="form-control" onChange={handleFileChange} />
                    </div>
                </div>
                <div className="row">
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">Cập nhật</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
