import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';

const initialState = {
    ten_phong_kham: "",
    dia_chi: "",
    so_dien_thoai: "",
    id_khoa: "",
    id_bac_si: "",
    mo_ta: "",
    hinh_anh_phong_kham: "",
};

export default function EditPhongKham() {
    const [state, setState] = useState(initialState);
    const [khoaOptions, setKhoaOptions] = useState([]);
    const [bacSiOptions, setBacSiOptions] = useState([]);
    const [file, setFile] = useState(null);
    const { ten_phong_kham, dia_chi, so_dien_thoai, id_khoa, id_bac_si, mo_ta, hinh_anh_phong_kham } = state;
    const { id_phong_kham } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:5000/api/getphongkham/${id_phong_kham}`)
            .then((resp) => setState({ ...resp.data[0] }));
        
        const fetchOptions = async () => {
            try {
                const khoaResponse = await axios.get("http://localhost:5000/api/getalldmkhoa");
                setKhoaOptions(khoaResponse.data);

                const bacSiResponse = await axios.get("http://localhost:5000/api/getallbs");
                setBacSiOptions(bacSiResponse.data);
            } catch (error) {
                toast.error("Lỗi khi lấy dữ liệu");
            }
        };
        fetchOptions();
    }, [id_phong_kham]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setState({ ...state, hinh_anh_phong_kham: `/images/${file.name}` });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Kiểm tra các trường bắt buộc
        if (!ten_phong_kham || !dia_chi || !so_dien_thoai || !id_khoa || !id_bac_si || !mo_ta || !hinh_anh_phong_kham) {
            toast.error("Vui lòng nhập đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin phòng khám?")) {
                // Tạo dữ liệu JSON để gửi đi
                const data = {
                    ten_phong_kham,
                    dia_chi,
                    so_dien_thoai,
                    id_khoa,
                    id_bac_si,
                    mo_ta,
                    hinh_anh_phong_kham
                };
                
                try {
                    // Gửi yêu cầu cập nhật thông tin phòng khám
                    await axios.put(`http://localhost:5000/api/updatephongkham/${id_phong_kham}`, data);
                    toast.success("Cập nhật thông tin phòng khám thành công!");
                    setTimeout(() => navigate("/IndexPhongKham"), 500);
                } catch (err) {
                    toast.error(err.response?.data || "Lỗi xảy ra trong quá trình cập nhật");
                }
            }
        }
    };

    return (
        <div>
            <h1 className="mb-0">Cập nhật phòng khám</h1>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="row mb-3">
                    <div className="col">
                        <label className="form-label">Tên phòng khám</label>
                        <input type="text" name="ten_phong_kham" onChange={handleInputChange} value={ten_phong_kham || ""} className="form-control" placeholder="Tên phòng khám" />
                    </div>
                    <div className="col">
                        <label className="form-label">Số điện thoại</label>
                        <input type="text" name="so_dien_thoai" onChange={handleInputChange} value={so_dien_thoai || ""} className="form-control" placeholder="Số điện thoại" />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className="col">
                        <label className="form-label">Địa chỉ</label>
                        <input type="text" name="dia_chi" onChange={handleInputChange} value={dia_chi || ""} className="form-control" placeholder="Địa chỉ" />
                    </div>
                    <div className="col">
                        <label className="form-label">Mô tả</label>
                        <textarea name="mo_ta" onChange={handleInputChange} value={mo_ta || ""} className="form-control" placeholder="Mô tả" />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className="col">
                        <label className="form-label">ID Khoa</label>
                        <select name="id_khoa" onChange={handleInputChange} value={id_khoa || ""} className="form-control">
                            <option value="">Chọn khoa</option>
                            {khoaOptions.map(khoa => (
                                <option key={khoa.id_khoa} value={khoa.id_khoa}>{khoa.ten_khoa}</option>
                            ))}
                        </select>
                    </div>
                    <div className="col">
                        <label className="form-label">ID Bác sĩ</label>
                        <select name="id_bac_si" onChange={handleInputChange} value={id_bac_si || ""} className="form-control">
                            <option value="">Chọn bác sĩ</option>
                            {bacSiOptions.map(bacSi => (
                                <option key={bacSi.id_bac_si} value={bacSi.id_bac_si}>{bacSi.ho_ten}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className='row mb-3'>
                    <img style={{ borderRadius: '10px', marginLeft: '10px' }} src={hinh_anh_phong_kham} width='150' height='180' className="img img-responsive" />
                    <div className="col">
                        <label className="form-label">Hình ảnh phòng khám</label>
                        <input type="file" name="hinh_anh_phong_kham" onChange={handleFileChange} className="form-control" />
                    </div>
                </div>
                <div className="row">
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px' }} className="btn btn-warning">Cập nhật</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
