import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const initiaState = {
  ho_ten_bn: "",
  ngay_dat_lich: "",
  tong_tien: "",
  trang_thai: "",
  dia_chi_bn: "",
  ghi_chu: "",
  sdt_bn: "",
  ngay_kham: "",
  id_bac_si: "",
};

const formatCurrency = (number) => {
  return new Intl.NumberFormat("vi-VN", {
    style: "currency",
    currency: "VND",
  }).format(number);
};

export default function EditHD() {
  const [state, setState] = useState(initiaState);

  const {
    ho_ten_bn,
    ngay_dat_lich,
    tong_tien,
    trang_thai,
    dia_chi_bn,
    ghi_chu,
    sdt_bn,
    ngay_kham,
    id_bac_si,
  } = state;

  const { id_lich_kham } = useParams();
  const [bacSiData, setBacSiData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    if (id_bac_si) {
      axios
        .get(`http://localhost:5000/api/getnv/${id_bac_si}`)
        .then((resp) => setBacSiData(resp.data))
        .catch((error) => toast.error("Không thể lấy thông tin bác sĩ"));
    }
  }, [id_bac_si]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/getlk/${id_lich_kham}`)
      .then((resp) => setState({ ...resp.data[0] }));
  }, [id_lich_kham]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!trang_thai) {
      toast.error("Vui lòng nhập đầy đủ thông tin");
    } else {
      if (window.confirm("Bạn có muốn cập nhật thông tin  ?")) {
        axios
          .put(`http://localhost:5000/api/updatehd/${id_lich_kham}`, {
            trang_thai,
          })
          .then(() => {
            setState({ trang_thai: "" });
          })
          .catch((err) => toast.error(err.response.data));
        toast.success("Sửa hóa đơn thành công !");
        setTimeout(() => navigate("/Indexhd"), 500);
      }
    }
  };
  return (
    <div>
      <h3 class="mb-0">Cập nhật lịch khám</h3>
      <hr />
      <form onSubmit={handleSubmit} enctype="multipart/form-data">
        <div class="row">
          <div class="col mb-3">
            <label class="form-label">Ngày đặt khám</label>
            <input
              type="date"
              name="ngay_dat_lich"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Ngày đặt khám"
              value={ngay_dat_lich?.slice(0, 10) || ""}
            />
          </div>
          <div class="col mb-3">
            <label class="form-label">Ngày khám</label>
            <input
              type="date"
              name="ngay_kham"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Ngày đặt khám"
              value={ngay_kham?.slice(0, 10) || ""}
            />
          </div>
        </div>
        <div class="row">
          <div class="col mb-3">
            <label class="form-label">Tên bênh nhân</label>
            <input
              type="text"
              name="ho_ten_bn"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Tên bệnh nhân"
              value={ho_ten_bn || ""}
            />
          </div>
          <div class="col mb-3">
            <label class="form-label">Số điện thoại</label>
            <input
              type="text"
              name="sdt_bn"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Số điện thoại"
              value={sdt_bn || ""}
            />
          </div>
        </div>
        <div class="row">
          <div class="col mb-3">
            <label class="form-label">Bác sĩ</label>
            <input
              type="text"
              name="id_bac_si"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Bác sĩ khám bệnh"
              value={id_bac_si || ""}
            />
          </div>

          <div class="col mb-3">
            <select
              style={{ marginTop: "31px" }}
              name="trang_thai"
              className="form-control"
              onChange={handleInputChange}
              value={trang_thai}
            >
              <option value="2">Đã Duyệt</option>
              <option value="1">Chưa Duyệt</option>
              <option value="3">Đang sắp xếp lịch</option>
            </select>
          </div>
        </div>
        <div class="row">
          <div class="col mb-3">
            <label class="form-label">Địa chỉ</label>
            <input
              type="text"
              name="soluong"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Địa chỉ"
              value={dia_chi_bn || ""}
            />
          </div>
          <div class="col mb-3">
            <label class="form-label">Tổng tiền</label>
            <input
              type="text"
              name="ma_danh_muc"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Tổng tiền"
              value={formatCurrency(tong_tien) || ""}
            />
          </div>
        </div>
        <div className="row">
          <div class="col mb-3">
            <label class="form-label">Mô tả</label>
            <input
              type="text"
              name="ghi_chu"
              class="form-control"
              onChange={handleInputChange}
              placeholder="Ghi chú"
              value={ghi_chu || ""}
            />
          </div>
        </div>
        <div class="row">
          <div class="d-grid">
            <button
              style={{ marginLeft: "10px", marginTop: "30px" }}
              class="btn btn-warning"
            >
              Cập nhật
            </button>
          </div>
          <div class="d-grid">
            <button
              style={{ marginLeft: "10px", marginTop: "30px" }}
              class="btn btn-warning"
            >
              In Đơn{" "}
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
