import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Indexadddichvu() {
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);

    const loadData = async () => {
        try {
            const [lichKhamRes, dichVuRes] = await Promise.all([
                axios.get("http://localhost:5000/api/getalllichkham"),
                axios.get("http://localhost:5000/api/getallgdv")
            ]);
            
            const lichKhamData = lichKhamRes.data;
            const dichVuData = dichVuRes.data;

            // Lọc các gói dịch vụ có id_khoa trùng với chuyen_khoa trong lịch khám
            const filtered = dichVuData.filter(dichVu =>
                lichKhamData.some(lichKham => lichKham.chuyen_khoa === dichVu.id_khoa)
            );

            setData(dichVuData); // Lưu tất cả dữ liệu dịch vụ
            setFilteredData(filtered); // Lưu dữ liệu dịch vụ đã lọc
        } catch (error) {
            console.error("Error loading data", error);
        }
    };

    useEffect(() => {
        loadData();
    }, []);

    const formatCurrency = (number) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
    };

    return (
        <div className="card shadow mb-4">
            <div className="d-flex align-items-center justify-content-between card-header py-3">
                <h6 className="m-0 font-weight-bold text-primary">
                    <Link to="/Indexadddichvukham">Tất cả dịch vụ</Link> / <Link to="/Indexadddichvutheochuyenkhoa">Dịch vụ theo chuyên khoa đã đặt</Link>
                </h6>
            </div>
            <div className="card-body">
                <div className="table-responsive">
                    <table className="table table-bordered" width="100%" cellSpacing="0">
                        <thead>
                            <tr>
                                <th>STT</th>
                                <th>Hình ảnh</th>
                                <th>Tên dịch vụ</th>
                                <th>Giá</th>
                                <th>Chi tiết dịch vụ</th>
                                <th>Lựa chọn</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredData.map((item, index) => (
                                <tr key={item.id_dich_vu}>
                                    <td>{index + 1}</td>
                                    <td>
                                        <img
                                            style={{ borderRadius: '5px' }}
                                            src={item.hinh_anh_dv}
                                            width="60"
                                            height="60"
                                            className="img img-responsive"
                                            alt={item.ten_dich_vu}
                                        />
                                    </td>
                                    <td>{item.ten_dich_vu}</td>
                                    <td>{formatCurrency(item.gia)}</td>
                                    <td>
                                        <Link to={`/Viewgdv/${item.id_dich_vu}`} className="btn btn-primary">Chi Tiết</Link>
                                    </td>
                                    <td>
                                        <input type="checkbox" value={item.id_dich_vu} />
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div className="row">
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '20px' }} className="btn btn-primary">
                            Thêm dịch vụ khám sử dụng
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}
