import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initiaState = {
    ho_ten_bn: "",
    ngay_dat_lich: "",
    tong_tien: "",
    trang_thai: "",
    dia_chi_bn: "",
    ghi_chu: "",
    sdt_bn: "",
    ngay_kham: "",
    id_bac_si: "",
    id_phong_kham: "",
    id_khoa: "",  // Thêm trường id_khoa để lưu thông tin chuyên khoa
};

export default function EditLichKhamBenh() {
    const [state, setState] = useState(initiaState);
    const [khoaList, setKhoaList] = useState([]);  // State để lưu danh sách khoa

    const { ho_ten_bn, ngay_dat_lich, trang_thai, dia_chi_bn, ghi_chu, sdt_bn, ngay_kham, id_phong_kham, id_khoa } = state;
    const { id_lich_kham } = useParams();
    const navigate = useNavigate();

    // Lấy thông tin lịch khám
    useEffect(() => {
        axios.get(`http://localhost:5000/api/getlk/${id_lich_kham}`)
            .then((resp) => {
                setState({ ...resp.data[0] });
            })
            .catch((error) => toast.error("Không thể lấy thông tin lịch khám"));
    }, [id_lich_kham]);

    // Lấy danh sách chuyên khoa từ API
    useEffect(() => {
        axios.get("http://localhost:5000/api/getalldmkhoa")
            .then((resp) => setKhoaList(resp.data))
            .catch((error) => toast.error("Không thể tải danh sách chuyên khoa"));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!trang_thai) {
            toast.error("Vui lòng nhập đầy đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin?")) {
                axios.put(`http://localhost:5000/api/updatelk/${id_lich_kham}`, 
                  { trang_thai, id_khoa })  // Truyền thêm id_khoa để cập nhật chuyên khoa
                    .then(() => {
                        setState({ trang_thai: "", id_khoa: "" });
                        toast.success("Cập nhật lịch khám thành công!");
                        setTimeout(() => navigate("/Indexlichkham"), 500);
                    })
                    .catch((err) => toast.error(err.response.data));
            }
        }
    };

    return (
        <div>
            <h3 className="mb-0">Cập nhật lịch khám</h3>
            <hr />
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Ngày đặt khám</label>
                        <input 
                            type="date" 
                            name="ngay_dat_lich" 
                            className="form-control" 
                            onChange={handleInputChange} 
                            placeholder="Ngày đặt khám" 
                            value={ngay_dat_lich?.slice(0, 10) || ""} 
                        />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Ngày khám</label>
                        <input 
                            type="date" 
                            name="ngay_kham" 
                            className="form-control" 
                            onChange={handleInputChange} 
                            placeholder="Ngày khám" 
                            value={ngay_kham?.slice(0, 10) || ""} 
                        />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Tên bệnh nhân</label>
                        <input 
                            type="text" 
                            name="ho_ten_bn" 
                            className="form-control" 
                            onChange={handleInputChange} 
                            placeholder="Tên bệnh nhân" 
                            value={ho_ten_bn || ""} 
                        />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Số điện thoại</label>
                        <input 
                            type="text" 
                            name="sdt_bn" 
                            className="form-control" 
                            onChange={handleInputChange} 
                            placeholder="Số điện thoại" 
                            value={sdt_bn || ""} 
                        />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Địa chỉ</label>
                        <input 
                            type="text" 
                            name="dia_chi_bn" 
                            className="form-control" 
                            onChange={handleInputChange} 
                            placeholder="Địa chỉ" 
                            value={dia_chi_bn || ""} 
                        />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Trạng thái lịch khám</label>
                        <select
                            name="trang_thai"
                            className="form-control"
                            onChange={handleInputChange}
                            value={trang_thai}
                        >
                            <option value="2">Đã Duyệt</option>
                            <option value="1">Chưa Duyệt</option>
                            <option value="3">Đang sắp xếp lịch</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Chuyên khoa khám bệnh</label>
                        <select
                            name="id_khoa"
                            className="form-control"
                            onChange={handleInputChange}
                            value={id_khoa}  // Sử dụng id_khoa làm giá trị ban đầu
                        >
                            {khoaList.map((khoa) => (
                                <option key={khoa.id_khoa} value={khoa.id_khoa}>
                                    {khoa.ten_khoa}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Phòng khám</label>
                        <select
                            name="id_phong_kham"
                            className="form-control"
                            onChange={handleInputChange}
                            value={id_phong_kham}
                        >
                            <option value="1">Phòng khám tổng quát</option>
                            <option value="2">Phòng khám trẻ em</option>
                            <option value="3">Phòng khám xét nghiệm</option>
                            <option value="4">Phòng khám dinh dưỡng</option>
                            <option value="5">Phòng khám tiền hôn nhân</option>
                            <option value="6">Phòng khám tư vấn người lớn</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Ghi chú</label>
                        <input 
                            type="text" 
                            name="ghi_chu" 
                            className="form-control" 
                            onChange={handleInputChange} 
                            placeholder="Ghi chú" 
                            value={ghi_chu || ""} 
                        />
                    </div>
