import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom"; // Import useNavigate
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Indexdm from "./page/Khoa/indexdm";  // Các component khác của bạn
import Sizebar from "./components/Sidebar/sizebar"; 
import Footer from "./components/Footter/footer"; 
import Navbar from "./components/Navbar/navbar"; 
import LoginForm from "./page/Login/login"; 

// Các route khác của bạn...

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Khởi tạo useNavigate

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token); // nếu có token thì cho phép truy cập các route
  }, []);

  const handleLogin = (token) => {
    localStorage.setItem("token", token);
    setIsLoggedIn(true);
    navigate("/"); // Điều hướng về trang chính sau khi đăng nhập thành công
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/login"); // Điều hướng về trang đăng nhập khi đăng xuất
  };

  return (
    <BrowserRouter>
      {/* Đây là container chính của bạn */}
      <ToastContainer />
      {!isLoggedIn ? (
        <Routes>
          <Route path="/login" element={<LoginForm onLogin={handleLogin} />} />
          <Route path="*" element={<LoginForm onLogin={handleLogin} />} />
        </Routes>
      ) : (
        <div className="app">
          <div id="wrapper">
            {/* Sidebar */}
            <Sizebar />

            <div id="content-wrapper" className="d-flex flex-column">
              <div id="content">
                <Navbar onLogout={handleLogout} />
                <div className="container-fluid">
                  <Routes>
                    <Route path="/" element={<Thongke />} />
                    {/* Các Routes khác */}
                    <Route path="/Indexdm" element={<Indexdm />} />
                    {/* Các Route khác */}
                  </Routes>
                </div>
              </div>
            </div>
          </div>
          <Footer />
        </div>
      )}
    </BrowserRouter>
  );
}
