import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';

// Thiết lập giá trị khởi tạo cho các trường trong bảng `kho_thuoc`
const initiaState = {
  ten_thuoc_vat_tu: "",
  ngay_nhap: "",
  so_luong: "",
  anh_sanpham: "" // Trường này sẽ lưu đường dẫn ảnh tải lên
};

export default function Editkhohang() {
  const [state, setState] = useState(initiaState);
  const [file, setFile] = useState(null);

  // Lấy các biến từ state
  const { ten_thuoc_vat_tu, ngay_nhap, so_luong, anh_sanpham } = state;

  const { id_kho } = useParams();
  const navigate = useNavigate();

  // Lấy dữ liệu kho hàng từ API khi `id_kho` thay đổi
  useEffect(() => {
    axios.get(`http://localhost:5000/api/getkhohang/${id_kho}`)
      .then((resp) => setState({ ...resp.data[0] }));
  }, [id_kho]);

  // Xử lý thay đổi dữ liệu cho các trường input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };

  // Xử lý thay đổi ảnh tải lên
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFile(file);
    setState({ ...state, anh_sanpham: `/images/${file.name}` });
  };

  // Xử lý khi gửi form cập nhật kho hàng
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!ten_thuoc_vat_tu || !ngay_nhap || !so_luong) {
      toast.error("Vui lòng nhập đủ thông tin");
    } else {
      if (window.confirm("Bạn có muốn cập nhật thông tin?")) {
        axios.put(`http://localhost:5000/api/updatekhohang/${id_kho}`, {
          ten_thuoc_vat_tu,
          ngay_nhap,
          so_luong,
          anh_sanpham
        }).then(() => {
          setState({ ten_thuoc_vat_tu: "", ngay_nhap: "", so_luong: "", anh_sanpham: "" });
          toast.success("Sửa thông tin kho hàng thành công!");
          setTimeout(() => navigate("/Indexkhohang"), 500);
        }).catch((err) => toast.error(err.response.data));
      }
    }
  };

  return (
    <div>
      <h1 className="mb-0">Cập nhật kho hàng</h1>
      <hr />
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="row">
          <div className="col mb-3">
            <label className="form-label">Tên thuốc/vật tư</label>
            <input
              type="text"
              name="ten_thuoc_vat_tu"
              className="form-control"
              placeholder="Tên thuốc/vật tư"
              onChange={handleInputChange}
              value={ten_thuoc_vat_tu || ""}
            />
          </div>
          <div className="col mb-3">
            <label className="form-label">Ngày nhập kho</label>
            <input
              type="date"
              name="ngay_nhap"
              className="form-control"
              placeholder="Ngày nhập kho"
              onChange={handleInputChange}
              value={ngay_nhap || ""}
            />
          </div>
        </div>
        <div className="row">
          <div className="col mb-3">
            <label className="form-label">Số lượng</label>
            <input
              type="text"
              name="so_luong"
              className="form-control"
              placeholder="Số lượng"
              onChange={handleInputChange}
              value={so_luong || ""}
            />
          </div>
        </div>
        <div className="row">
          <img
            style={{ borderRadius: '10px', marginLeft: '10px' }}
            src={anh_sanpham}
            width="150"
            height="180"
            alt="Ảnh sản phẩm"
            className="img img-responsive"
          />
          <div className="col mb-3">
            <label className="form-label">Ảnh sản phẩm</label>
            <input
              type="file"
              name="anh_sanpham"
              className="form-control"
              onChange={handleFileChange}
              placeholder="Ảnh sản phẩm"
            />
          </div>
        </div>
        <div className="row">
          <div className="d-grid">
            <button
              style={{ marginLeft: '10px', marginTop: '30px' }}
              className="btn btn-warning"
            >
              Cập nhật
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}
