import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

export default function Viewhoadon() {
    const formatCurrency = (number) => {
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(number);
    };  
    const [chiTiet ,setData] = useState([]);

    const{id_lich_kham} = useParams();

    useEffect(() => {
        // Thay đổi cách lấy dữ liệu thành một mảng
        axios.get(`http://localhost:5000/api/getctlk/${id_lich_kham}`)
            .then((resp) => setData(resp.data))
            .catch((error) => console.error(error));
    }, [id_lich_kham]);
  return (
    <div>
            <div class="card shadow mb-4">
        <div class="d-flex align-items-center justify-content-between card-header py-3">
            <h6 class="m-0 font-weight-bold text-primary">Chi tiết lịch </h6>
        </div>
        <div class="card-body">
            <div class="table-responsive">
                <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                    <thead>
                        <tr>
                            <th>Hình ảnh</th>
                            <th>Tên dịch vụ</th>
                            <th>Số lượng</th>
                            <th>Giá</th>

                        </tr>
                    </thead>
                    <tbody>
                        {chiTiet.map((item)=>{
                            return(
                            <tr key={item.id_hoa_don}>
                                <td><img style={{borderRadius: '5px'}} src={item.hinh_anh_dv} width='60' height='60' className="img img-responsive" /></td>
                                <td>{item.ten_dich_vu}</td>
                                <td>{item.so_luong}</td>
                                <td>{formatCurrency(item.gia)}</td>
                                
                            </tr>)
                        })}
                        

                    </tbody>

               
                </table>
            </div>
        </div>
    </div>
    </div>
  )
}
