import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initiaState = {
    ho_ten_bn: "",
    ngay_dat_lich: "",
    tong_tien: "",
    trang_thai: "",
    dia_chi_bn: "",
    ghi_chu: "",
    sdt_bn: "",
    ngay_kham: "",
    id_bac_si: "",
    id_phong_kham :"",
};

const itiaState = {
    ho_ten: "",
};

export default function EditLichKhamBenh() {
    const [state, setState] = useState(initiaState);
    const [bacSiData, setBacSiData] = useState(itiaState);
    const [dmKhoa, setDmKhoa] = useState([]); // Danh sách khoa khám bệnh
    const [lichKhamList, setLichKhamList] = useState([]); // Danh sách lịch khám
    const [dichVuStatus, setDichVuStatus] = useState("Chưa chọn dịch vụ"); // Trạng thái dịch vụ

    const { ho_ten_bn, ngay_dat_lich, trang_thai, dia_chi_bn, ghi_chu, sdt_bn, ngay_kham, id_phong_kham } = state;
    const { ho_ten } = bacSiData;

    const { id_lich_kham } = useParams();
    const navigate = useNavigate();

    // Lấy thông tin các khoa khám bệnh
    useEffect(() => {
        axios.get("http://localhost:5000/api/getalldmkhoa")
            .then((resp) => setDmKhoa(resp.data))
            .catch((error) => toast.error("Không thể lấy thông tin khoa"));
    }, []);

    // Lấy thông tin lịch khám
    useEffect(() => {
        axios.get(`http://localhost:5000/api/getlk/${id_lich_kham}`)
            .then((resp) => {
                setState({ ...resp.data[0] });
                if (resp.data[0].id_bac_si) {
                    axios.get(`http://localhost:5000/api/getbacsi/${resp.data[0].id_bac_si}`)
                        .then((resp) => setBacSiData({ ...resp.data[0] }))
                        .catch((error) => toast.error("Không thể lấy thông tin bác sĩ"));
                }
            })
            .catch((error) => toast.error("Không thể lấy thông tin lịch khám"));
    }, [id_lich_kham]);

    // Lấy danh sách lịch khám để kiểm tra trạng thái dịch vụ
    useEffect(() => {
        axios.get("http://localhost:5000/api/getalllk")
            .then((resp) => {
                setLichKhamList(resp.data);
                // Kiểm tra xem lịch khám hiện tại có nằm trong danh sách này không
                const hasDichVu = resp.data.some(lich => lich.id_lich_kham === parseInt(id_lich_kham));
                setDichVuStatus(hasDichVu ? "Đã sử dụng dịch vụ khám" : "Chưa chọn dịch vụ");
            })
            .catch((error) => toast.error("Không thể lấy danh sách lịch khám"));
    }, [id_lich_kham]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!trang_thai) {
            toast.error("Vui lòng nhập đầy đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin?")) {
                axios.put(`http://localhost:5000/api/updatelk/${id_lich_kham}`, { trang_thai })
                    .then(() => {
                        setState({ trang_thai: "" });
                        toast.success("Cập nhật lịch khám thành công!");
                        setTimeout(() => navigate("/Indexhd"), 500);
                    })
                    .catch((err) => toast.error(err.response.data));
            }
        }
    }

    return (
        <div>
            <h3 className="mb-0">Cập nhật lịch khám</h3>
            <hr />
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Ngày đặt khám</label>
                        <input type="date" name="ngay_dat_lich" className="form-control" onChange={handleInputChange} placeholder="Ngày đặt khám" value={ngay_dat_lich?.slice(0, 10) || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Ngày khám</label>
                        <input type="date" name="ngay_kham" className="form-control" onChange={handleInputChange} placeholder="Ngày khám" value={ngay_kham?.slice(0, 10) || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Tên bệnh nhân</label>
                        <input type="text" name="ho_ten_bn" className="form-control" onChange={handleInputChange} placeholder="Tên bệnh nhân" value={ho_ten_bn || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Số điện thoại</label>
                        <input type="text" name="sdt_bn" className="form-control" onChange={handleInputChange} placeholder="Số điện thoại" value={sdt_bn || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Địa chỉ</label>
                        <input type="text" name="dia_chi_bn" className="form-control" onChange={handleInputChange} placeholder="Địa chỉ" value={dia_chi_bn || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Trạng thái</label>
                        <select
                            style={{ marginTop: '31px' }}
                            name="trang_thai"
                            className="form-control"
                            onChange={handleInputChange}
                            value={trang_thai}
                        >
                            <option value="2">Đã Duyệt</option>
                            <option value="1">Chưa Duyệt</option>
                            <option value="3">Đang sắp xếp lịch</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Bác sĩ</label>
                        <input type="text" name="ho_ten" className="form-control" placeholder="Bác sĩ khám bệnh" value={ho_ten || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Phòng khám</label>
                        <select
                            style={{ marginTop: '31px' }}
                            name="id_phong_kham"
                            className="form-control"
                            onChange={handleInputChange}
                            value={id_phong_kham}
                        >
                            {dmKhoa.map(khoa => (
                                <option key={khoa.id_khoa} value={khoa.id_khoa}>{khoa.ten_khoa}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Dịch vụ</label>
                        <input type="text" className="form-control" value={dichVuStatus} readOnly />
                    </div>
                </div>
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Ghi chú</label>
                        <input type="text" name="ghi_chu" className="form-control" onChange={handleInputChange} placeholder="Ghi chú" value={ghi_chu || ""} />
                    </div>
                </div>
                <div className="row">
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">Cập nhật</button>
                    </div>
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">In Đơn</button>
                    </div>
                </div>
            </form>
        </div>
    )
}
