import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

const initialState = {
    ho_ten_bn: "",
    ngay_dat_lich: "",
    tong_tien: "",
    trang_thai: "",
    dia_chi_bn: "",
    ghi_chu: "",
    sdt_bn: "",
    ngay_kham: "",
    id_bac_si: "",
    id_phong_kham: "",
    chuyen_khoa: "",
};

export default function EditLichKhamBenh() {
    const [state, setState] = useState(initialState);
    const [dsBacSi, setDsBacSi] = useState([]);
    const [dsKhoa, setDsKhoa] = useState([]);
    const { id_lich_kham } = useParams();
    const navigate = useNavigate();

    // Lấy thông tin lịch khám
    useEffect(() => {
        axios.get(`http://localhost:5000/api/getlk/${id_lich_kham}`)
            .then((resp) => {
                const data = resp.data[0];
                setState(data);
                if (data?.chuyen_khoa) {
                    fetchDoctorsByKhoa(data.chuyen_khoa);
                }
            })
            .catch(() => toast.error("Không thể lấy thông tin lịch khám"));
    }, [id_lich_kham]);

    // Lấy danh sách khoa
    useEffect(() => {
        axios.get('http://localhost:5000/api/getalldmkhoa')
            .then((resp) => setDsKhoa(resp.data))
            .catch(() => toast.error("Không thể lấy danh sách khoa"));
    }, []);

    // Lấy danh sách bác sĩ theo id_khoa
    const fetchDoctorsByKhoa = (id_khoa) => {
        axios.get(`http://localhost:5000/api/getdoctors/${id_khoa}`)
            .then((resp) => setDsBacSi(resp.data))
            .catch(() => toast.error("Không thể lấy danh sách bác sĩ"));
    }

    // Đồng bộ id_bac_si với danh sách bác sĩ khi dsBacSi thay đổi
    useEffect(() => {
        if (dsBacSi.length && state.id_bac_si) {
            const bacSiExists = dsBacSi.some(bacSi => bacSi.id_bac_si === state.id_bac_si);
            if (!bacSiExists) {
                setState(prev => ({ ...prev, id_bac_si: "" })); // Đặt lại nếu không tồn tại
            }
        }
    }, [dsBacSi, state.id_bac_si]);

    // Xử lý thay đổi chọn chuyên khoa
    const handleKhoaChange = (e) => {
        const selectedKhoa = e.target.value;
        setState({ ...state, chuyen_khoa: selectedKhoa, id_bac_si: "" });
        fetchDoctorsByKhoa(selectedKhoa);
    }

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!state.trang_thai) {
            toast.error("Vui lòng nhập đầy đủ thông tin");
        } else {
            if (window.confirm("Bạn có muốn cập nhật thông tin?")) {
                axios.put(`http://localhost:5000/api/updatelk/${id_lich_kham}`, { trang_thai: state.trang_thai })
                    .then(() => {
                        setState({ ...initialState });
                        toast.success("Cập nhật lịch khám thành công!");
                        setTimeout(() => navigate("/Indexlichkham"), 500);
                    })
                    .catch((err) => toast.error(err.response.data));
            }
        }
    }

    return (
        <div>
            <h3 className="mb-0">Cập nhật lịch khám</h3>
            <hr />
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Ngày đặt khám</label>
                        <input type="date" name="ngay_dat_lich" className="form-control" onChange={handleInputChange} placeholder="Ngày đặt khám" value={state.ngay_dat_lich?.slice(0, 10) || ""} />
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Ngày khám</label>
                        <input type="date" name="ngay_kham" className="form-control" onChange={handleInputChange} placeholder="Ngày khám" value={state.ngay_kham?.slice(0, 10) || ""} />
                    </div>
                </div>
                {/* Other fields */}
                <div className="row">
                    <div className="col mb-3">
                        <label className="form-label">Chuyên khoa khám bệnh</label>
                        <select name="chuyen_khoa" className="form-control" onChange={handleKhoaChange} value={state.chuyen_khoa}>
                            {dsKhoa.map((khoa) => (
                                <option key={khoa.id_khoa} value={khoa.id_khoa}>
                                    {khoa.ten_khoa}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="col mb-3">
                        <label className="form-label">Bác sĩ</label>
                        <select name="id_bac_si" className="form-control" onChange={handleInputChange} value={state.id_bac_si}>
                            <option value="">Chọn bác sĩ</option>
                            {dsBacSi.map((bacSi) => (
                                <option key={bacSi.id_bac_si} value={bacSi.id_bac_si}>
                                    {bacSi.ho_ten}
                                </option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="d-grid">
                        <button style={{ marginLeft: '10px', marginTop: '30px' }} className="btn btn-warning">Cập nhật</button>
                    </div>
                </div>
            </form>
        </div>
    );
}
