import React from 'react';
import  { useState, useEffect } from "react";
import './AIchat.css'
const ChatAIApp = () => {
  const [chats, setChats] = useState([]);
  const [theme, setTheme] = useState("dark_mode");
  const [userMessage, setUserMessage] = useState("");
  const [isResponseGenerating, setIsResponseGenerating] = useState(false);

  const API_KEY = "YOUR_API_KEY"; // Replace with your actual API key
  const API_URL = `https://generativelanguage.googleapis.com/v1/models/gemini-pro:generateContent?key=${API_KEY}`;

  // Load saved chats and theme from local storage
  useEffect(() => {
    const savedChats = JSON.parse(localStorage.getItem("saved-chats")) || [];
    const savedTheme = localStorage.getItem("themeColor") || "dark_mode";

    setChats(savedChats);
    setTheme(savedTheme);
    document.body.classList.toggle("light_mode", savedTheme === "light_mode");
  }, []);

  // Save chats and theme to local storage whenever they change
  useEffect(() => {
    localStorage.setItem("saved-chats", JSON.stringify(chats));
    localStorage.setItem("themeColor", theme);
  }, [chats, theme]);

  const toggleTheme = () => {
    const newTheme = theme === "dark_mode" ? "light_mode" : "dark_mode";
    setTheme(newTheme);
    document.body.classList.toggle("light_mode", newTheme === "light_mode");
  };

  const handleSendMessage = async () => {
    if (!userMessage.trim() || isResponseGenerating) return;

    const newChats = [
      ...chats,
      { type: "outgoing", text: userMessage },
    ];
    setChats(newChats);
    setUserMessage("");
    setIsResponseGenerating(true);

    // Send the message to the API
    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          contents: [
            {
              role: "user",
              parts: [{ text: userMessage }],
            },
          ],
        }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error.message);

      const apiResponse = data?.candidates[0].content.parts[0].text.replace(
        /\*\*(.*?)\*\*/g,
        "$1"
      );
      setChats((prevChats) => [
        ...prevChats,
        { type: "incoming", text: apiResponse },
      ]);
    } catch (error) {
      setChats((prevChats) => [
        ...prevChats,
        { type: "error", text: error.message },
      ]);
    } finally {
      setIsResponseGenerating(false);
    }
  };

  const handleDeleteChats = () => {
    if (window.confirm("Bạn có chắc chắn muốn xóa toàn bộ đoạn chat?")) {
      setChats([]);
    }
  };
  return (
    <body id='ai-chat'>
        <div className='ai-all'>
        <p className="ai-subtitle"> GPT AI health care</p>

        {/* AI Suggestion list */}
        <ul className="ai-suggestion-list">
          <li className="ai-suggestion">
            <h4 className="ai-text">
              Nếu tôi bị ho cần điều trị như thế nào để tình hình sức khỏe được cải thiện tốt hơn ?
            </h4>
            <span className="ai-icon material-symbols-rounded">draw</span>
          </li>
          <li className="ai-suggestion">
            <h4 className="ai-text">
              Nếu tôi bị sổ mũi thì phải xử lý như thế nào ?
            </h4>
            <span className="ai-icon material-symbols-rounded">lightbulb</span>
          </li>
          <li className="ai-suggestion">
            <h4 className="ai-text">
              Bạn có thể tư vấn giúp tôi kế hoạch giảm cân ?
            </h4>
            <span className="ai-icon material-symbols-rounded">explore</span>
          </li>
          <li className="ai-suggestion">
            <h4 className="ai-text">
              Tư vấn thói quen sinh hoạt lành mạnh giúp sức khỏe trở nên tốt hơn.
            </h4>
            <span className="ai-icon material-symbols-rounded">code</span>
          </li>
        </ul>

      {/* AI Chat List / Container */}
      <div className="ai-chat-list"></div>

      {/* AI Typing Area */}
      <div className="ai-typing-area">
        <form action="#" className="ai-typing-form">
          <div className="ai-input-wrapper">
            <input
              type="text"
              placeholder="Nhập câu hỏi của bạn tại đây..."
              className="ai-typing-input"
              required
            />
            <button
              id="send-ai-message-button"
              className="ai-icon material-symbols-rounded"
            >
              send
            </button>
          </div>
          <div className="ai-action-buttons">
            <span id="theme-ai-toggle-button" className="ai-icon material-symbols-rounded">
              light_mode
            </span>
            <span id="delete-ai-chat-button" className="ai-icon material-symbols-rounded">
              delete
            </span>
          </div>
        </form>
      </div>
    </div>
    </body>
    
  );
};

export default ChatAIApp;
