import React, { useState } from 'react';
import QrScanner from 'react-qr-scanner'; // Import thư viện QR scanner
import './Qr.css';

const QRPayment = () => {
  const [isVisible, setIsVisible] = useState(true); // State để kiểm soát hiển thị
  const [isScanned, setIsScanned] = useState(false); // Kiểm tra xem QR đã được quét
  const [qrData, setQrData] = useState(null); // Lưu dữ liệu từ mã QR

  // Hàm xử lý khi mã QR được quét
  const handleScan = (data) => {
    if (data) {
      setQrData(data); // Lưu dữ liệu mã QR
      setIsScanned(true); // Đánh dấu mã QR đã được quét
      console.log('Dữ liệu mã QR:', data); // In dữ liệu vào console
    }
  };

  // Hàm xử lý lỗi quét QR
  const handleError = (err) => {
    console.error('Lỗi khi quét mã QR:', err); // In lỗi vào console
  };

  // Hàm ẩn giao diện khi nhấn nút "X"
  const handleHideQR = () => {
    setIsVisible(false); // Cập nhật state để ẩn giao diện
  };

  if (!isVisible) {
    return null; // Không render gì nếu isVisible là false
  }

  return (
    <div className="vietqr">
      <div className="vietqr-modal">
        <h2 className="vietqr-title">Mã VietQR thanh toán</h2>
        <div className="vietqr-content">
          <div className="vietqr-frame">
            {isScanned ? (
              <div>
                <h3 className="vietqr-header">Mã QR đã được quét thành công!</h3>
                <p><strong>Dữ liệu:</strong> {qrData}</p>
              </div>
            ) : (
              <>
                <h3 className="vietqr-header">Mở Ứng Dụng Ngân Hàng Quét QRCode</h3>
                <QrScanner
                  delay={300} // Thời gian giữa các lần quét (ms)
                  style={{ width: '100%' }}
                  onError={handleError} // Gọi khi có lỗi
                  onScan={handleScan} // Gọi khi quét thành công
                />
              </>
            )}
            <div className="vietqr-info">
              <p><strong>Số tiền:</strong> 100,000 VND</p>
              <p><strong>Tên chủ TK:</strong> Đinh Việt Hùng</p>
              <p><strong>Số TK:</strong> 0343493518</p>
            </div>
            <p className="vietqr-footer">
              Giải pháp được cung cấp trên nền tảng <strong>Sapo</strong>
            </p>
          </div>
        </div>
      </div>
      <div className="qr-delete" onClick={handleHideQR}>
        <img src="https://img.icons8.com/?size=100&id=9433&format=png&color=000000" alt="X" />
      </div>
    </div>
  );
};

export default QRPayment;
