import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

// Component đăng ký khám bệnh
function RegistrationForm({ onRegistrationComplete }) {
  const [formData, setFormData] = useState({
    phone: '',
    dateOfBirth: '',
    fullName: '',
    gender: 'Nam',
    nationality: 'VIET NAM (Việt Nam)',
    city: '',
    district: '',
    ward: '',
    address: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onRegistrationComplete(formData); // Gọi hàm từ props để gửi dữ liệu
  };

  return (
    <div className="form-container-xn">
      <h2>ĐĂNG KÝ KHÁM BỆNH</h2>
      <p>
        Vui lòng nhập thông tin cá nhân để hoàn tất thủ tục...
      </p>
      
      <form onSubmit={handleSubmit}>
        <div className="form-group-xn">
          <label htmlFor="phone">* Số điện thoại</label>
          <input
            type="text"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group-xn">
          <label htmlFor="dateOfBirth">* Ngày sinh</label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group-xn">
          <label htmlFor="fullName">* Họ và tên</label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group-xn">
          <label>* Giới tính</label>
          <div className="gender-group-xn">
            <label>
              <input
                type="radio"
                name="gender"
                value="Nam"
                checked={formData.gender === 'Nam'}
                onChange={handleChange}
                required
              />
              Nam
            </label>
            <label>
              <input
                type="radio"
                name="gender"
                value="Nữ"
                checked={formData.gender === 'Nữ'}
                onChange={handleChange}
              />
              Nữ
            </label>
          </div>
        </div>

        <div className="form-group-xn">
          <label>* Quốc tịch</label>
          <select
            name="nationality"
            value={formData.nationality}
            onChange={handleChange}
            required
          >
            <option value="VIET NAM (Việt Nam)">VIET NAM (Việt Nam)</option>
            <option value="KHÁC">Khác</option>
          </select>
        </div>

        <div className="form-group-xn">
          <label>* Tỉnh thành</label>
          <select
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          >
            <option value="">Chọn tỉnh thành</option>
            <option value="Hà Nội">Hà Nội</option>
            <option value="TP Hồ Chí Minh">TP Hồ Chí Minh</option>
          </select>
        </div>

        <div className="form-group-xn">
          <label>* Quận huyện</label>
          <select
            name="district"
            value={formData.district}
            onChange={handleChange}
            required
          >
            <option value="">Chọn quận huyện</option>
            <option value="Ba Đình">Ba Đình</option>
            <option value="Tân Bình">Tân Bình</option>
          </select>
        </div>

        <div className="form-group-xn">
          <label>* Phường xã</label>
          <select
            name="ward"
            value={formData.ward}
            onChange={handleChange}
            required
          >
            <option value="">Chọn phường xã</option>
            <option value="Phường Liễu Giai">Phường Liễu Giai</option>
            <option value="Phường 1">Phường 1</option>
          </select>
        </div>

        <div className="form-group-xn">
          <label>* Số nhà, tên đường</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            required
          />
        </div>

        <div className="button-group-xn">
          <Link to={'/datlich'}>
            <button type="button" className="back-btn-xn">QUAY LẠI</button>
          </Link>
          
          <button type="submit" className="submit-btn-xn">ĐẶT HẸN</button>
        </div>
      </form>
    </div>
  );
}

// Component đặt lịch hẹn
function AppointmentForm({ registrationData }) {
  const [departments, setDepartments] = useState([]); // State for departments list
  const [doctors, setDoctors] = useState([]); // State for doctors based on selected department
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [healthCondition, setHealthCondition] = useState('');
  const [message, setMessage] = useState(''); // State for feedback message

  // Fetch departments from API on component mount
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getalldm');
        if (!response.ok) {
          throw new Error('Failed to fetch departments');
        }
        const data = await response.json();
        setDepartments(data); // Update state with fetched data
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };
    fetchDepartments();
  }, []);

  // Fetch doctors based on selected department
  useEffect(() => {
    if (selectedSpecialty) {
      const fetchDoctors = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/getdoctors/${selectedSpecialty}`);
          if (!response.ok) {
            throw new Error('Failed to fetch doctors');
          }
          const data = await response.json();
          setDoctors(data); // Update doctors state based on department selection
        } catch (error) {
          console.error('Error fetching doctors:', error);
        }
      };
      fetchDoctors();
    } else {
      setDoctors([]); // Reset doctors list if no department is selected
    }
  }, [selectedSpecialty]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = {
      ...registrationData, // Thêm thông tin đăng ký
      selectedDoctor,
      selectedSpecialty,
      appointmentDate,
      healthCondition,
    };

    try {
      const response = await fetch('http://localhost:5000/api/addAppointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to submit appointment');
      }

      const result = await response.json();
      setMessage('Đặt lịch thành công!'); // Hiển thị thông báo thành công
      console.log('Form Data Submitted:', result);
    } catch (error) {
      setMessage('Đặt lịch không thành công, vui lòng thử lại.'); // Hiển thị thông báo lỗi
      console.error('Error submitting appointment:', error);
    }
  };

  return (
    <div className="form-container-dl">
      <h2>ĐẶT LỊCH KHÁM BỆNH</h2>
      <p>Quý khách hàng có nhu cầu đặt hẹn khám tại Hệ thống phòng khám đa khoa Phố Nối</p>

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit} className="appointment-form-dl">
        <div className="form-group-dl">
          <label>* Chuyên khoa</label>
          <select
            value={selectedSpecialty}
            onChange={(e) => setSelectedSpecialty(e.target.value)}
            required
          >
            <option value="">Chọn chuyên khoa</option>
            {departments.map((department) => (
              <option key={department.id_khoa} value={department.id_khoa}>
                {department.ten_khoa}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label>* Bác sĩ</label>
          <select
            value={selectedDoctor}
            onChange={(e) => setSelectedDoctor(e.target.value)}
            required
            disabled={!selectedSpecialty} // Disable if no specialty is selected
          >
            <option value="">Chọn bác sĩ</option>
            {doctors.map((doctor) => (
              <option key={doctor.id_bac_si} value={doctor.id_bac_si}>
                {doctor.ho_ten}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label>* Ngày hẹn</label>
          <input
            type="date"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
            required
          />
        </div>

        <div className="form-group-dl">
          <label>Triệu chứng sức khỏe</label>
          <textarea
            value={healthCondition}
            onChange={(e) => setHealthCondition(e.target.value)}
            placeholder="Mô tả triệu chứng của bạn..."
          />
        </div>

        <div className="button-group-dl">
          <button type="submit" className="submit-btn-dl">ĐẶT HẸN</button>
        </div>
      </form>
    </div>
  );
}

// Component chính cho trang đặt lịch
function AppointmentPage() {
  const [registrationData, setRegistrationData] = useState(null);

  const handleRegistrationComplete = (data) => {
    setRegistrationData(data); // Lưu thông tin đăng ký
  };

  return (
    <div className="appointment-page">
      <h1>Đặt Lịch Khám</h1>
      {!registrationData ? (
        <RegistrationForm onRegistrationComplete={handleRegistrationComplete} />
      ) : (
        <AppointmentForm registrationData={registrationData} />
      )}
    </div>
  );
}

export default AppointmentPage;
