import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";


export default function Message() {
  const [messages, setMessages] = useState([
    { id: 1, text: "Chào bác sĩ, tôi có một số câu hỏi về sức khỏe.", sender: "patient" },
    { id: 2, text: "Chào bạn! Bạn có thể cho tôi biết vấn đề cụ thể của bạn không?", sender: "doctor" },
    { id: 3, text: "Tôi cảm thấy đau đầu và chóng mặt.", sender: "patient" },
    { id: 4, text: "Bạn có gặp phải triệu chứng nào khác không?", sender: "doctor" },
    { id: 5, text: "Tôi cảm thấy hơi mệt", sender: "patient" },
  ]);

  const [newMessage, setNewMessage] = useState("");
  const chatRef = useRef(null);

  // Tự động cuộn xuống tin nhắn mới nhất
  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      const updatedMessages = [
        ...messages,
        { id: messages.length + 1, text: newMessage.trim(), sender: "patient" },
      ];
      setMessages(updatedMessages);
      setNewMessage("");

      // Giả lập phản hồi của bác sĩ
      setTimeout(() => {
        const doctorReply = {
          id: updatedMessages.length + 1,
          text: "Cảm ơn bạn đã chia sẻ, tôi sẽ tư vấn ngay!",
          sender: "doctor",
        };
        setMessages((prevMessages) => [...prevMessages, doctorReply]);
      }, 1500);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">Trò chuyện</div>
      <div className="chat-body" ref={chatRef}>
        {messages.map((msg) => (
          <div
            key={msg.id}
            className={`chat-message ${msg.sender === "doctor" ? "doctor" : "patient"}`}
          >
            {msg.text}
          </div>
        ))}
      </div>
      <form className="chat-input" onSubmit={sendMessage}>
        <input
          type="text"
          placeholder="Nhập tin nhắn..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button type="submit">Gửi</button>
      </form>

      <div className="chat-buttons">
        {/* Biểu tượng Messenger */}
        <button className="btn-contact">
          <svg width="40" height="40" viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
            <path
              fill="#1292ff"
              d="M44,23.5C44,34.27,35.05,43,24,43c-1.65,0-3.25-0.19-4.78-0.56L13.46,44.77C12.33,45.34,11,44.51,11,43.25v-4.03..."
            />
          </svg>
        </button>

        {/* Biểu tượng Zalo */}
        <button className="btn-zalo">
          <svg width="40" height="40" viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
            <path
              fill="#2962ff"
              d="M15,36V6.827l-1.211-0.811C8.64,8.083,5,13.112,5,19v10c0,7.732,6.268,14,14,14h10	c4.722,0,8.883-2.348,11.417..."
            />
          </svg>
        </button>

        {/* Biểu tượng Chat AI */}
        <Link to="/chatwithAI">
          <button className="btn-chat-ai">
            <svg width="40" height="40" viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
              <path
                fill="url(#gradient)"
                d="M46.332,23.147l-2.016-0.999C42.852,16.838,39.115,11.762,34,8.762V7.781..."
              />
            </svg>
          </button>
        </Link>
      </div>
    </div>
  );
}
