import React, { useState, useEffect } from 'react';
import './AIchat.css';

const ChatAIApp = () => {
  const [userMessage, setUserMessage] = useState('');
  const [isResponseGenerating, setIsResponseGenerating] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [theme, setTheme] = useState(localStorage.getItem("themeColor") === "light_mode" ? "light_mode" : "dark_mode");

  const API_KEY = "AIzaSyCVqQlTN7ylrRha-gzAdggRmsSPpyNfm0A"; // Your API key here
  const API_URL = `https://generativelanguage.googleapis.com/v1/models/gemini-pro:generateContent?key=${API_KEY}`;

  // Load chat history from local storage
  useEffect(() => {
    const savedChats = localStorage.getItem("saved-chats");
    if (savedChats) {
      setChatHistory(JSON.parse(savedChats));
    }
  }, []);

  // Handle sending outgoing chat messages
  const handleOutgoingChat = (e) => {
    e.preventDefault();
    if (!userMessage.trim() || isResponseGenerating) return;

    setIsResponseGenerating(true);

    // Add user message to chat history
    const newChatHistory = [...chatHistory, { sender: 'user', message: userMessage }];
    setChatHistory(newChatHistory);
    localStorage.setItem('saved-chats', JSON.stringify(newChatHistory));

    // Clear input
    setUserMessage('');

    // Fetch the response from the API
    generateAPIResponse();
  };

  // Show typing effect and handle API response
  const generateAPIResponse = async () => {
    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          contents: [{ role: "user", parts: [{ text: userMessage }] }],
        }),
      });
      const data = await response.json();

      if (!response.ok) throw new Error(data.error.message);

      const apiResponse = data?.candidates[0].content.parts[0].text.replace(/\*\*(.*?)\*\*/g, "$1");

      // Add AI response to chat history
      const newChatHistory = [
        ...chatHistory,
        { sender: 'ai', message: apiResponse }
      ];
      setChatHistory(newChatHistory);
      localStorage.setItem('saved-chats', JSON.stringify(newChatHistory));

    } catch (error) {
      setIsResponseGenerating(false);
      console.error("Error generating response:", error);
    } finally {
      setIsResponseGenerating(false);
    }
  };

  // Toggle theme
  const toggleTheme = () => {
    const newTheme = theme === 'light_mode' ? 'dark_mode' : 'light_mode';
    setTheme(newTheme);
    localStorage.setItem('themeColor', newTheme);
  };

  // Delete chat history
  const deleteChatHistory = () => {
    if (window.confirm("Sau khi xóa sẽ không thể hoàn tác. Bạn có muốn xóa toàn bộ đoạn chat này về mặc định?")) {
      setChatHistory([]);
      localStorage.removeItem('saved-chats');
    }
  };

  return (
    <div id='ai-chat' className={theme}>
      <div className='ai-all'>
        <p className="ai-subtitle">GPT AI Health Care</p>

        {/* AI Suggestion list */}
        <ul className="ai-suggestion-list">
          <li className="ai-suggestion">
            <h4 className="ai-text" onClick={() => setUserMessage('Nếu tôi bị ho cần điều trị như thế nào để tình hình sức khỏe được cải thiện tốt hơn ?')}>
              Nếu tôi bị ho cần điều trị như thế nào để tình hình sức khỏe được cải thiện tốt hơn?
            </h4>
          </li>
          <li className="ai-suggestion">
            <h4 className="ai-text" onClick={() => setUserMessage('Nếu tôi bị sổ mũi thì phải xử lý như thế nào ?')}>
              Nếu tôi bị sổ mũi thì phải xử lý như thế nào ?
            </h4>
          </li>
          <li className="ai-suggestion">
            <h4 className="ai-text" onClick={() => setUserMessage('Bạn có thể tư vấn giúp tôi kế hoạch giảm cân ?')}>
              Bạn có thể tư vấn giúp tôi kế hoạch giảm cân ?
            </h4>
          </li>
          <li className="ai-suggestion">
            <h4 className="ai-text" onClick={() => setUserMessage('Tư vấn thói quen sinh hoạt lành mạnh giúp sức khỏe trở nên tốt hơn.')}>
              Tư vấn thói quen sinh hoạt lành mạnh giúp sức khỏe trở nên tốt hơn.
            </h4>
          </li>
        </ul>

        {/* AI Chat List / Container */}
        <div className="ai-chat-list">
          {chatHistory.map((chat, index) => (
            <div key={index} className={`message ${chat.sender}`}>
              <div className="message-content">
                <img className="avatar" src={chat.sender === 'user' ? "https://i.imgur.com/3xFFbKJ.jpeg" : "https://img.icons8.com/?size=512&id=iGqse5s20iex&format=png"} alt={`${chat.sender} avatar`} />
                <p className="text">{chat.message}</p>
              </div>
            </div>
          ))}
        </div>

        {/* AI Typing Area */}
        <div className="ai-typing-area">
          <form onSubmit={handleOutgoingChat} className="ai-typing-form">
            <div className="ai-input-wrapper">
              <input
                type="text"
                value={userMessage}
                onChange={(e) => setUserMessage(e.target.value)}
                placeholder="Nhập câu hỏi của bạn tại đây..."
                className="ai-typing-input"
                required
              />
              <button
                type="submit"
                id="send-ai-message-button"
                className="ai-icon material-symbols-rounded"
                disabled={isResponseGenerating}
              >
                send
              </button>
            </div>
            <div className="ai-action-buttons">
              <span onClick={toggleTheme} id="theme-ai-toggle-button" className="ai-icon material-symbols-rounded">
                {theme === 'light_mode' ? 'dark_mode' : 'light_mode'}
              </span>
              <span onClick={deleteChatHistory} id="delete-ai-chat-button" className="ai-icon material-symbols-rounded">
                delete
              </span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ChatAIApp;
