function AppointmentForm({ registrationData }) {
  const [departments, setDepartments] = useState([]); // State for departments list
  const [doctors, setDoctors] = useState([]); // State for doctors based on selected department
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [healthCondition, setHealthCondition] = useState('');
  const [message, setMessage] = useState(''); // State for feedback message

  // Fetch departments from API on component mount
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getalldm');
        if (!response.ok) {
          throw new Error('Failed to fetch departments');
        }
        const data = await response.json();
        setDepartments(data); // Update state with fetched data
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };
    fetchDepartments();
  }, []);

  // Fetch doctors based on selected department
  useEffect(() => {
    if (selectedSpecialty) {
      const fetchDoctors = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/getdoctors/${selectedSpecialty}`);
          if (!response.ok) {
            throw new Error('Failed to fetch doctors');
          }
          const data = await response.json();
          setDoctors(data); // Update doctors state based on department selection
        } catch (error) {
          console.error('Error fetching doctors:', error);
        }
      };
      fetchDoctors();
    } else {
      setDoctors([]); // Reset doctors list if no department is selected
    }
  }, [selectedSpecialty]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = {
      ...registrationData, // Thêm thông tin đăng ký
      selectedDoctor,
      selectedSpecialty,
      appointmentDate,
      healthCondition,
    };

    try {
      const response = await fetch('http://localhost:5000/api/addAppointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to submit appointment');
      }

      const result = await response.json();
      setMessage('Đặt lịch thành công!'); // Hiển thị thông báo thành công
      console.log('Form Data Submitted:', result);
    } catch (error) {
      setMessage('Đặt lịch không thành công, vui lòng thử lại.'); // Hiển thị thông báo lỗi
      console.error('Error submitting appointment:', error);
    }
  };

  return (
    <div className="form-container-dl">
      <h2>ĐẶT LỊCH KHÁM BỆNH</h2>
      <p>Quý khách hàng có nhu cầu đặt hẹn khám tại Hệ thống phòng khám đa khoa Phố Nối</p>

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit} className="appointment-form-dl">
        <div className="form-group-dl">
          <label>Chọn loại dịch vụ khám</label>
          <div className="radio-group-dl">
            <label>
              <input type="radio" name="service" value="Khám trong giờ" required /> Khám trong giờ
            </label>
            <label>
              <input type="radio" name="service" value="Khám ngoài giờ" required /> Khám ngoài giờ
            </label>
          </div>
        </div>

        <div className="form-group-dl">
          <label htmlFor="specialty">* Chuyên khoa</label>
          <select
            id="specialty"
            value={selectedSpecialty}
            onChange={(e) => setSelectedSpecialty(e.target.value)}
            required
          >
            <option value="">Chọn chuyên khoa</option>
            {departments.map((department) => (
              <option key={department.id_khoa} value={department.id_khoa}>
                {department.ten_khoa}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label htmlFor="doctor">* Bác sĩ</label>
          <select
            id="doctor"
            value={selectedDoctor}
            onChange={(e) => setSelectedDoctor(e.target.value)}
            required
            disabled={!selectedSpecialty} // Disable if no specialty is selected
          >
            <option value="">Chọn bác sĩ</option>
            {doctors.map((doctor) => (
              <option key={doctor.id_bac_si} value={doctor.id_bac_si}>
                {doctor.ho_ten}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label htmlFor="appointmentDate">* Ngày hẹn</label>
          <input
            type="date"
            id="appointmentDate"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
            required
          />
        </div>

        <div className="form-group-dl">
          <label htmlFor="healthCondition">Triệu chứng sức khỏe</label>
          <textarea
            id="healthCondition"
            value={healthCondition}
            onChange={(e) => setHealthCondition(e.target.value)}
            placeholder="Mô tả triệu chứng của bạn..."
          />
        </div>

        <div className="button-group-dl">
          <button type="submit" className="submit-btn-dl">ĐẶT HẸN</button>
        </div>
      </form>
    </div>
  );
}

// Component chính cho trang đặt lịch
function AppointmentPage() {
  const [registrationData, setRegistrationData] = useState(null);

  const handleRegistrationComplete = (data) => {
    setRegistrationData(data); // Lưu thông tin đăng ký
  };

  return (
    <div className="appointment-page">
      <h1>Đặt Lịch Khám</h1>
      {!registrationData ? (
        <RegistrationForm onRegistrationComplete={handleRegistrationComplete} />
      ) : (
        <AppointmentForm registrationData={registrationData} />
      )}
    </div>
  );
}

export default AppointmentPage;
