import React, { useState } from 'react';

export default function Message() {
  const [messages, setMessages] = useState([
    { id: 1, text: 'Chào bác sĩ, tôi có một số câu hỏi về sức khỏe.', sender: 'patient' },
    { id: 2, text: 'Chào bạn! Bạn có thể cho tôi biết vấn đề cụ thể của bạn không?', sender: 'doctor' },
    { id: 3, text: 'Tôi cảm thấy đau đầu và chóng mặt.', sender: 'patient' },
    { id: 4, text: 'Bạn có gặp phải triệu chứng nào khác không?', sender: 'doctor' },
    { id: 5, text: 'Tôi cảm thấy hơi mệt', sender: 'patient' }
  ]);

  const [newMessage, setNewMessage] = useState('');

  const sendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      setMessages([...messages, { id: messages.length + 1, text: newMessage, sender: 'patient' }]);
      setNewMessage('');
    }
  };

  return (
    <div>
      <button className="btn-contact">
        <svg color="inherit" viewBox="0 0 32 32" className="lc-1mpchac">
          <path fill="#FFFFFF" d="M12.63,26.46H8.83a6.61,6.61,0,0,1-6.65-6.07,89.05,89.05,0,0,1,0-11.2A6.5,6.5,0,0,1,8.23,3.25a121.62,121.62,0,0,1,15.51,0A6.51,6.51,0,0,1,29.8,9.19a77.53,77.53,0,0,1,0,11.2,6.61,6.61,0,0,1-6.66,6.07H19.48L12.63,31V26.46"></path>
          <path fill="#DA3807" d="M19.57,21.68h3.67a2.08,2.08,0,0,0,2.11-1.81,89.86,89.86,0,0,0,0-10.38,1.9,1.9,0,0,0-1.84-1.74,113.15,113.15,0,0,0-15,0A1.9,1.9,0,0,0,6.71,9.49a74.92,74.92,0,0,0-.06,10.38,2,2,0,0,0,2.1,1.81h3.81V26.5Z" className="lc-1adcsh3 e1nep2br0"></path>
        </svg>
      </button>
      <div className="message">
        <div className="message-header">
          <span>
            <svg color="#111111" viewBox="0 0 32 32" aria-hidden="true" className="lc-1mpchac">
              <path d="M8,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S9.1,18,8,18z M16,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S17.1,18,16,18z M24,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S25.1,18,24,18z"></path>
            </svg>
          </span>
          <h3>Chat Cùng Bác Sĩ</h3>
          <span className="hide-message">
            <svg color="inherit" viewBox="0 0 32 32" aria-hidden="true" className="lc-1mpchac">
              <path d="M11,22c-0.6,0-1-0.4-1-1s0.4-1,1-1h15c0.6,0,1,0.4,1,1s-0.4,1-1,1H11z"></path>
            </svg>
          </span>
        </div>
        <div className="message-body">
          {messages.map((message) => (
            <div key={message.id} className={`message-item ${message.sender}`}>
              <p>{message.text}</p>
            </div>
          ))}
        </div>
        <div className="message-footer">
          <form onSubmit={sendMessage}>
            <div className="input-container">
              <textarea
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Viết tin nhắn"
                className="message-input"
              />
              <button type="submit" className="send-button">
                <svg color="inherit" viewBox="0 0 32 32" className="lc-1mpchac">
                  <path d="M16,6l0.3,0c5.4,0.1,9.7,4.6,9.7,10c0,5.5-4.5,10-10,10S6,21.5,6,16S10.5,6,16,6z M16,8l-0.2,0 C11.4,8.1,8,11.7,8,16s3.4,7.9,7.8,8l0.2,0c4.4-0.1,8-3.6,8-8S20.4,8.1,16,8z M14.2,11.1c0-0.7,0.6-1.2,1.3-1.2 c0.7,0,1.2,0.6,1.2,1.2v6.1c0,0.7-0.6,1.2-1.2,1.2C14.7,17.4,14.2,17,14.2,16.3V11.1z M19.5,12c0.8,0,1.5,0.7,1.5,1.5 S20.3,15,19.5,15S18,14.3,18,13.5S18.7,12,19.5,12z"></path>
                </svg>
              </button>
            </div>
          </form>
        </div>
      </div>

      <style jsx>{`
        .message {
          display: flex;
          flex-direction: column;
          border: 1px solid #ccc;
          border-radius: 8px;
          padding: 16px;
          width: 400px; /* Adjust width as needed */
          margin: 20px auto; /* Center the message box */
        }
        .message-header {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .message-body {
          flex-grow: 1;
          margin: 10px 0;
          overflow-y: auto; /* Allow scrolling for long messages */
          max-height: 200px; /* Set max height for the messages area */
        }
        .message-item {
          border-radius: 8px;
          padding: 10px;
          margin: 5px 0;
          width: fit-content; /* Adjust width to fit content */
          max-width: 80%; /* Limit max width */
        }
        .patient {
          background-color: #e0f7fa; /* Light blue for patient */
          align-self: flex-start; /* Align left */
        }
        .doctor {
          background-color: #ffccbc; /* Light pink for doctor */
          align-self: flex-end; /* Align right */
        }
        .message-footer {
          display: flex;
          align-items: center;
        }
        .input-container {
          display: flex;
          flex-grow: 1;
          align-items: center;
        }
        .message-input {
          flex-grow: 1;
          border: 1px solid #ccc;
          border-radius: 4px;
          padding: 5px; /* Smaller padding */
          margin-right: 10px;
          resize: none; /* Prevent resizing */
          height: 30px; /* Fixed height for input */
          font-size: 14px; /* Adjust font size */
        }
        .send-button {
          background-color: blue;
          color: white;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          padding: 5px 10px; /* Smaller button */
        }
      `}</style>
    </div>
  );
}
