import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export default function Message() {
  const [messages, setMessages] = useState([
    { id: 1, text: 'Chào bác sĩ, tôi có một số câu hỏi về sức khỏe.', sender: 'patient' },
    { id: 2, text: 'Chào bạn! Bạn có thể cho tôi biết vấn đề cụ thể của bạn không?', sender: 'doctor' },
    { id: 3, text: 'Tôi cảm thấy đau đầu và chóng mặt.', sender: 'patient' },
    { id: 4, text: 'Bạn có gặp phải triệu chứng nào khác không?', sender: 'doctor' },
    { id: 5, text: 'Tôi cảm thấy hơi mệt', sender: 'patient' },
  ]);

  const [newMessage, setNewMessage] = useState('');

  const sendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      const updatedMessages = [
        ...messages,
        { id: messages.length + 1, text: newMessage, sender: 'patient' },
      ];
      setMessages(updatedMessages);
      setNewMessage('');

      // Giả lập phản hồi của bác sĩ
      setTimeout(() => {
        const doctorReply = {
          id: updatedMessages.length + 1,
          text: 'Cảm ơn bạn đã chia sẻ, tôi sẽ tư vấn ngay!',
          sender: 'doctor',
        };
        setMessages((prevMessages) => [...prevMessages, doctorReply]);
      }, 1500);
    }
  };

  return (
    <div className="message-container">
      <div className="message-box">
        {messages.map((msg) => (
          <div
            key={msg.id}
            className={`message-item ${msg.sender === 'doctor' ? 'doctor-message' : 'patient-message'}`}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <form className="message-form" onSubmit={sendMessage}>
        <input
          type="text"
          placeholder="Nhập tin nhắn..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="message-input"
        />
        <button type="submit" className="send-button">
          Gửi
        </button>
      </form>

      {/* Biểu tượng liên lạc */}
      <div className="button-container">
        {/* Messenger */}
        <button className="btn-contact">
          <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="100" height="100" viewBox="0 0 48 48">
            <radialGradient id="messengerGradient" cx="11.087" cy="7.022" r="47.612" gradientTransform="matrix(1 0 0 -1 0 50)" gradientUnits="userSpaceOnUse">
              <stop offset="0" stopColor="#1292ff"></stop>
              <stop offset=".946" stopColor="#ff6257"></stop>
            </radialGradient>
            <path
              fill="url(#messengerGradient)"
              d="M44,23.5C44,34.27,35.05,43,24,43c-1.651,0-3.25-0.194-4.784-0.564	c-0.465-0.112-0.951-0.069-1.379,0.145L13.46,44.77C12.33,45.335,11,44.513,11,43.249v-4.025c0-0.575-0.257-1.111-0.681-1.499	C6.425,34.165,4,29.11,4,23.5C4,12.73,12.95,4,24,4S44,12.73,44,23.5z"
            ></path>
          </svg>
        </button>

        {/* Zalo */}
        <button className="btn-zalo">
          <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="100" height="100" viewBox="0 0 48 48">
            <path
              fill="#2962ff"
              d="M15,36V6.827l-1.211-0.811C8.64,8.083,5,13.112,5,19v10c0,7.732,6.268,14,14,14h10	c4.722,0,8.883-2.348,11.417-5.931V36H15z"
            ></path>
          </svg>
        </button>

        {/* Chat AI */}
        <Link to={'/chatwithAI'}>
          <button className="btn-chat-ai">
            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="100" height="100" viewBox="0 0 48 48">
              <linearGradient id="chatAIGradient" x1=".029" x2="48.667" y1="40.428" y2="9.662" gradientUnits="userSpaceOnUse">
                <stop offset=".002" stopColor="#9c55d4"></stop>
                <stop offset="1" stopColor="#07e6ff"></stop>
              </linearGradient>
              <path
                fill="url(#chatAIGradient)"
                d="M46.332,23.147l-2.016-0.999C42.852,16.838,39.115,11.762,34,8.762V7.781	c0-0.459-0.312-0.859-0.757-0.97L27.94,5.485C26.652,5.163,25.328,5,24,5s-2.652,0.163-3.94,0.485l-5.614,1.327	C14.312,7.061,14,7.461,14,7.919v0.843C8.883,11.751,5.147,16.829,3.684,22.144l-2.016,0.999c-0.36,0.179-0.591,0.548-0.615,0.955	c-0.024,0.406,0.17,0.789,0.502,1.01l2.021,1.335C4.364,31.068,7.211,35.8,11.5,39.169V40c0,0.386,0.224,0.738,0.572,0.895	l6.352,2.862C20.193,43.819,22.077,44,24,44s3.807-0.181,5.576-0.543l6.352-2.862C36.776,40.738,37,40.386,37,40v-0.843	c4.289-3.369,7.136-8.101,8.924-13.724l2.021-1.335c0.333-0.221,0.526-0.604,0.502-1.01C46.923,23.695,46.692,23.326,46.332,23.147z"
              ></path>
            </svg>
          </button>
        </Link>
      </div>
    </div>
  );
}
