import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';

function AppointmentForm() {
  const [departments, setDepartments] = useState([]); // State for departments list
  const [doctors, setDoctors] = useState([]); // State for doctors based on selected department
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [healthCondition, setHealthCondition] = useState('');
  const history = useHistory(); // Use history for navigation

  // Fetch departments from API on component mount
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getalldm');
        if (!response.ok) throw new Error('Failed to fetch departments');
        const data = await response.json();
        setDepartments(data); // Update state with fetched data
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };
    fetchDepartments();
  }, []);

  // Fetch doctors based on selected department
  useEffect(() => {
    if (selectedSpecialty) {
      const fetchDoctors = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/getdoctors/${selectedSpecialty}`);
          if (!response.ok) throw new Error('Failed to fetch doctors');
          const data = await response.json();
          setDoctors(data); // Update doctors state based on department selection
        } catch (error) {
          console.error('Error fetching doctors:', error);
        }
      };
      fetchDoctors();
    } else {
      setDoctors([]); // Reset doctors list if no department is selected
    }
  }, [selectedSpecialty]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save the form data to local storage
    const formData = {
      selectedDoctor,
      selectedSpecialty,
      appointmentDate,
      healthCondition,
    };
    localStorage.setItem('appointmentData', JSON.stringify(formData)); // Store the data
    // Navigate to registration form
    history.push('/xndatlich'); // Navigate to the registration form
  };

  return (
    <div className="form-container-dl">
      <h2>ĐĂNG KÝ KHÁM BỆNH</h2>
      <p>Quý khách hàng có nhu cầu đặt hẹn khám tại Hệ thống phòng khám đa khoa Phố Nối</p>

      <form onSubmit={handleSubmit} className="appointment-form-dl">
        <div className="form-group-dl">
          <label>Chọn loại dịch vụ khám</label>
          <div className="radio-group-dl">
            <label>
              <input type="radio" name="service" value="Khám trong giờ" /> Khám trong giờ
            </label>
            <label>
              <input type="radio" name="service" value="Khám ngoài giờ" /> Khám ngoài giờ
            </label>
            <label>
              <input type="radio" name="service" value="Khám online" /> Khám online
            </label>
          </div>
        </div>

        <div className="form-group-dl">
          <label>Chọn chuyên khoa</label>
          <select
            value={selectedSpecialty}
            onChange={(e) => setSelectedSpecialty(e.target.value)}
          >
            <option value="">-- Chọn chuyên khoa --</option>
            {departments.map((dept) => (
              <option key={dept.id_khoa} value={dept.id_khoa}>
                {dept.ten_khoa}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label>Chọn bác sĩ</label>
          <select
            value={selectedDoctor}
            onChange={(e) => setSelectedDoctor(e.target.value)}
          >
            <option value="">-- Chọn bác sĩ --</option>
            {doctors.map((doctor) => (
              <option key={doctor.id_bac_si} value={doctor.ho_ten}>
                {doctor.ho_ten} ({doctor.chuc_danh})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label>Chọn ngày - khung giờ muốn khám</label>
          <input
            type="date"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
          />
        </div>

        <div className="form-group-dl">
          <label>Nhập vấn đề sức khỏe cần khám</label>
          <textarea
            placeholder="Nhập tình trạng sức khỏe của bạn"
            value={healthCondition}
            onChange={(e) => setHealthCondition(e.target.value)}
          ></textarea>
        </div>

        <div className="button-group-dl">
          <button type="submit" className="submit-btn-dl">TIẾP THEO</button>
        </div>
      </form>
    </div>
  );
}

export default AppointmentForm;
