import React from 'react';
import  { useState, useEffect } from "react";
import './AIchat.css'
const ChatAIApp = () => {
  const [chats, setChats] = useState([]);
  const [theme, setTheme] = useState("dark_mode");
  const [userMessage, setUserMessage] = useState("");
  const [isResponseGenerating, setIsResponseGenerating] = useState(false);

  const API_KEY = "YOUR_API_KEY"; // Replace with your actual API key
  const API_URL = `https://generativelanguage.googleapis.com/v1/models/gemini-pro:generateContent?key=${API_KEY}`;

  // Load saved chats and theme from local storage
  useEffect(() => {
    const savedChats = JSON.parse(localStorage.getItem("saved-chats")) || [];
    const savedTheme = localStorage.getItem("themeColor") || "dark_mode";

    setChats(savedChats);
    setTheme(savedTheme);
    document.body.classList.toggle("light_mode", savedTheme === "light_mode");
  }, []);

  // Save chats and theme to local storage whenever they change
  useEffect(() => {
    localStorage.setItem("saved-chats", JSON.stringify(chats));
    localStorage.setItem("themeColor", theme);
  }, [chats, theme]);

  const toggleTheme = () => {
    const newTheme = theme === "dark_mode" ? "light_mode" : "dark_mode";
    setTheme(newTheme);
    document.body.classList.toggle("light_mode", newTheme === "light_mode");
  };

  const handleSendMessage = async () => {
    if (!userMessage.trim() || isResponseGenerating) return;

    const newChats = [
      ...chats,
      { type: "outgoing", text: userMessage },
    ];
    setChats(newChats);
    setUserMessage("");
    setIsResponseGenerating(true);

    // Send the message to the API
    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          contents: [
            {
              role: "user",
              parts: [{ text: userMessage }],
            },
          ],
        }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error.message);

      const apiResponse = data?.candidates[0].content.parts[0].text.replace(
        /\*\*(.*?)\*\*/g,
        "$1"
      );
      setChats((prevChats) => [
        ...prevChats,
        { type: "incoming", text: apiResponse },
      ]);
    } catch (error) {
      setChats((prevChats) => [
        ...prevChats,
        { type: "error", text: error.message },
      ]);
    } finally {
      setIsResponseGenerating(false);
    }
  };

  const handleDeleteChats = () => {
    if (window.confirm("Bạn có chắc chắn muốn xóa toàn bộ đoạn chat?")) {
      setChats([]);
    }
  };
  return (
    <body id='ai-chat'>
        import React, { useState, useEffect } from "react";
    </body>
    
  );
};

export default ChatAIApp;
