import React, { useState, useEffect } from 'react';
import './Qr.css';

const QRPayment = () => {
  const [isVisible, setIsVisible] = useState(true); // Kiểm soát hiển thị mã QR
  const [isScanned, setIsScanned] = useState(false); // Kiểm tra mã QR đã quét chưa

  const handleHideQR = () => {
    setIsVisible(false); // Ẩn mã QR khi không cần thiết
  };

  // Kiểm tra trạng thái mã QR mỗi 2 giây
  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const response = await fetch('http://localhost:5000/api/check-qr');
        const data = await response.json();
        if (data.isScanned) {
          setIsScanned(true); // Mã QR đã quét
          clearInterval(interval); // Dừng kiểm tra sau khi quét
        }
      } catch (error) {
        console.error('Lỗi khi kiểm tra trạng thái QR:', error);
      }
    }, 2000); // Kiểm tra mỗi 2 giây

    return () => clearInterval(interval); // Dọn dẹp interval khi component unmount
  }, []);

  if (!isVisible) {
    return null; // Không render mã QR nếu không cần thiết
  }

  return (
    <div className='vietqr'>
      <div className="vietqr-modal">
        <h2 className="vietqr-title">Mã VietQR thanh toán</h2>
        <div className="vietqr-content">
          <div className="vietqr-frame">
            <h3 className="vietqr-header">Mở Ứng Dụng Ngân Hàng Quét QRCode</h3>
            <img
              src="../Images/Qrthanhtoan.jpg" // Link tới ảnh QR thật
              alt="VietQR Code"
              className="vietqr-image"
            />
            <div className="vietqr-info">
              <p><strong>Số tiền:</strong> 100,000 VND</p>
              <p><strong>Tên chủ TK:</strong> Đinh Việt Hùng</p>
              <p><strong>Số TK:</strong> 0343493518</p>
            </div>
            <p className="vietqr-footer">
              Giải pháp được cung cấp trên nền tảng <strong>Sapo</strong>
            </p>
          </div>
        </div>
      </div>

      {isScanned && (
        <div className="scanned-message">
          <h3>Mã QR đã được quét!</h3>
        </div>
      )}

      <div className='qr-delete' onClick={handleHideQR}>
        <img src="https://img.icons8.com/?size=100&id=9433&format=png&color=000000" alt="" />
      </div>
    </div>
  );
};

export default QRPayment;
