import React, { useState, useEffect } from "react";

const ChatApp = () => {
  const [chats, setChats] = useState([]);
  const [theme, setTheme] = useState("dark_mode");
  const [userMessage, setUserMessage] = useState("");
  const [isResponseGenerating, setIsResponseGenerating] = useState(false);

  const API_KEY = "YOUR_API_KEY"; // Replace with your actual API key
  const API_URL = `https://generativelanguage.googleapis.com/v1/models/gemini-pro:generateContent?key=${API_KEY}`;

  // Load saved chats and theme from local storage
  useEffect(() => {
    const savedChats = JSON.parse(localStorage.getItem("saved-chats")) || [];
    const savedTheme = localStorage.getItem("themeColor") || "dark_mode";

    setChats(savedChats);
    setTheme(savedTheme);
    document.body.classList.toggle("light_mode", savedTheme === "light_mode");
  }, []);

  // Save chats and theme to local storage whenever they change
  useEffect(() => {
    localStorage.setItem("saved-chats", JSON.stringify(chats));
    localStorage.setItem("themeColor", theme);
  }, [chats, theme]);

  const toggleTheme = () => {
    const newTheme = theme === "dark_mode" ? "light_mode" : "dark_mode";
    setTheme(newTheme);
    document.body.classList.toggle("light_mode", newTheme === "light_mode");
  };

  const handleSendMessage = async () => {
    if (!userMessage.trim() || isResponseGenerating) return;

    const newChats = [
      ...chats,
      { type: "outgoing", text: userMessage },
    ];
    setChats(newChats);
    setUserMessage("");
    setIsResponseGenerating(true);

    // Send the message to the API
    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          contents: [
            {
              role: "user",
              parts: [{ text: userMessage }],
            },
          ],
        }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error.message);

      const apiResponse = data?.candidates[0].content.parts[0].text.replace(
        /\*\*(.*?)\*\*/g,
        "$1"
      );
      setChats((prevChats) => [
        ...prevChats,
        { type: "incoming", text: apiResponse },
      ]);
    } catch (error) {
      setChats((prevChats) => [
        ...prevChats,
        { type: "error", text: error.message },
      ]);
    } finally {
      setIsResponseGenerating(false);
    }
  };

  const handleDeleteChats = () => {
    if (window.confirm("Bạn có chắc chắn muốn xóa toàn bộ đoạn chat?")) {
      setChats([]);
    }
  };

  return (
    <div>
      {/* Header */}
      <header className="header">
        <h1 className="title">
          Xin chào: <span id="my-ip">Đang lấy địa chỉ IP...</span>
        </h1>
        <p className="subtitle">DinhVietHung.Com - GPT Health Care</p>
      </header>

      {/* Suggestion List */}
      <ul className="suggestion-list">
        {["Nếu tôi bị ho...", "Nếu tôi bị sổ mũi...", "Tư vấn giảm cân...", "Thói quen lành mạnh..."].map(
          (suggestion, index) => (
            <li
              key={index}
              className="suggestion"
              onClick={() => {
                setUserMessage(suggestion);
                handleSendMessage();
              }}
            >
              <h4 className="text">{suggestion}</h4>
              <span className="icon material-symbols-rounded">lightbulb</span>
            </li>
          )
        )}
      </ul>

      {/* Chat List */}
      <div className="chat-list">
        {chats.map((chat, index) => (
          <div
            key={index}
            className={`message ${chat.type === "outgoing" ? "outgoing" : "incoming"} ${
              chat.type === "error" ? "error" : ""
            }`}
          >
            <div className="message-content">
              <img
                className="avatar"
                src={
                  chat.type === "outgoing"
                    ? "https://i.imgur.com/3xFFbKJ.jpeg"
                    : "https://img.icons8.com/?size=512&id=iGqse5s20iex&format=png"
                }
                alt="avatar"
              />
              <p className="text">{chat.text}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Typing Area */}
      <div className="typing-area">
        <div className="input-wrapper">
          <input
            type="text"
            placeholder="Nhập câu hỏi của bạn tại đây..."
            className="typing-input"
            value={userMessage}
            onChange={(e) => setUserMessage(e.target.value)}
            onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
          />
          <button
            id="send-message-button"
            className="icon material-symbols-rounded"
            onClick={handleSendMessage}
            disabled={isResponseGenerating}
          >
            send
          </button>
        </div>
        <div className="action-buttons">
          <span
            id="theme-toggle-button"
            className="icon material-symbols-rounded"
            onClick={toggleTheme}
          >
            {theme}
          </span>
          <span
            id="delete-chat-button"
            className="icon material-symbols-rounded"
            onClick={handleDeleteChats}
          >
            delete
          </span>
        </div>
      </div>
    </div>
  );
};

export default ChatApp;
