import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    phone: '',
    dateOfBirth: '',
    fullName: '',
    customerID: '',
    gender: 'Nam',
    nationality: 'VIET NAM (Việt Nam)',
    city: '',
    district: '',
    ward: '',
    address: '',
  });

  const navigate = useNavigate(); // Sử dụng useNavigate thay cho useHistory

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const appointmentData = JSON.parse(localStorage.getItem('appointmentData')); // Retrieve appointment data

    const completeData = {
      ...appointmentData,
      ...formData,
    };

    // Log data to console to check if all required fields are filled
    console.log('Complete Data:', completeData);

    const requiredFields = ['phone', 'dateOfBirth', 'fullName', 'selectedDoctor', 'selectedSpecialty'];
    const missingFields = requiredFields.filter(field => !completeData[field]);

    if (missingFields.length > 0) {
      console.log('Missing fields:', missingFields);
      alert(`Vui lòng điền đầy đủ thông tin: ${missingFields.join(', ')}`);
      return; // Do not proceed if there are missing fields
    }

    try {
      const response = await fetch('http://localhost:5000/api/addAppointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(completeData),
      });

      if (!response.ok) {
        throw new Error('Failed to create appointment');
      }
      console.log('Appointment successfully created');

      // Sử dụng navigate để chuyển hướng sau khi thành công
      navigate('/confirmation'); // Chuyển hướng đến trang xác nhận (thay đổi đường dẫn nếu cần)
    } catch (error) {
      console.error('Error submitting appointment:', error);
    }
  };

  return (
    <div className="form-container-xn">
      <h2>ĐĂNG KÝ KHÁM BỆNH</h2>
      <p>Vui lòng nhập thông tin</p>
      
      <form onSubmit={handleSubmit}>
        <div className="form-group-xn">
          <label htmlFor="phone">* Số điện thoại</label>
          <input
            type="text"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group-xn">
          <label htmlFor="dateOfBirth">* Ngày sinh</label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group-xn">
          <label htmlFor="fullName">* Họ và tên</label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group-xn">
          <label htmlFor="customerID">Mã khách hàng (nếu có)</label>
          <input
            type="text"
            id="customerID"
            name="customerID"
            value={formData.customerID}
            onChange={handleChange}
          />
        </div>

        <div className="form-group-xn">
          <label>* Giới tính</label>
          <div className="gender-group-xn">
            <label>
              <input
                type="radio"
                name="gender"
                value="Nam"
                checked={formData.gender === 'Nam'}
                onChange={handleChange}
                required
              />
              Nam
            </label>
            <label>
              <input
                type="radio"
                name="gender"
                value="Nữ"
                checked={formData.gender === 'Nữ'}
                onChange={handleChange}
              />
              Nữ
            </label>
          </div>
        </div>

        <div className="form-group-xn">
          <label>* Quốc tịch</label>
          <select
            name="nationality"
            value={formData.nationality}
            onChange={handleChange}
            required
          >
            <option value="VIET NAM (Việt Nam)">VIET NAM (Việt Nam)</option>
            <option value="KHÁC">KHÁC</option>
          </select>
        </div>

        <div className="form-group-xn">
          <label htmlFor="address">Địa chỉ</label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
          />
        </div>

        <div className="button-group-xn">
          <button type="button" className="back-btn-xn">QUAY LẠI</button>
          <button type="submit" className="submit-btn-xn">ĐẶT HẸN</button>
        </div>
      </form>
    </div>
  );
}

export default RegistrationForm;
