import React, { useState } from 'react';

export default function Message() {
  const [messages, setMessages] = useState([
    { id: 1, text: 'Chào bác sĩ, tôi có một số câu hỏi về sức khỏe.', sender: 'patient' },
    { id: 2, text: 'Chào bạn! Bạn có thể cho tôi biết vấn đề cụ thể của bạn không?', sender: 'doctor' },
    { id: 3, text: 'Tôi cảm thấy đau đầu và chóng mặt.', sender: 'patient' },
    { id: 4, text: 'Bạn có gặp phải triệu chứng nào khác không?', sender: 'doctor' },
    { id: 5, text: 'Tôi cảm thấy hơi mệt', sender: 'patient' }
  ]);

  const [newMessage, setNewMessage] = useState('');

  const sendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      setMessages([...messages, { id: messages.length + 1, text: newMessage, sender: 'patient' }]);
      setNewMessage('');
    }
  };

  return (
    <div>
      <button className="btn-contact">
        <svg color="inherit" viewBox="0 0 32 32" className="lc-1mpchac">
          <path fill="#FFFFFF" d="M12.63,26.46H8.83a6.61,6.61,0,0,1-6.65-6.07,89.05,89.05,0,0,1,0-11.2A6.5,6.5,0,0,1,8.23,3.25a121.62,121.62,0,0,1,15.51,0A6.51,6.51,0,0,1,29.8,9.19a77.53,77.53,0,0,1,0,11.2,6.61,6.61,0,0,1-6.66,6.07H19.48L12.63,31V26.46"></path>
          <path fill="#DA3807" d="M19.57,21.68h3.67a2.08,2.08,0,0,0,2.11-1.81,89.86,89.86,0,0,0,0-10.38,1.9,1.9,0,0,0-1.84-1.74,113.15,113.15,0,0,0-15,0A1.9,1.9,0,0,0,6.71,9.49a74.92,74.92,0,0,0-.06,10.38,2,2,0,0,0,2.1,1.81h3.81V26.5Z" className="lc-1adcsh3 e1nep2br0"></path>
        </svg>
      </button>
      <div className="message">
        <div className="message-header">
          <span>
            <svg color="#111111" viewBox="0 0 32 32" aria-hidden="true" className="lc-1mpchac">
              <path d="M8,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S9.1,18,8,18z M16,18c-1.1,0-2-0.9-2-2s0.9-2,2-2 s2,0.9,2,2S17.1,18,16,18z M24,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S25.1,18,24,18z"></path>
            </svg>
          </span>
          <h3>Chat Cùng Bác Sĩ</h3>
          <span className="hide-message">
            <svg color="inherit" viewBox="0 0 32 32" aria-hidden="true" className="lc-1mpchac">
              <path d="M11,22c-0.6,0-1-0.4-1-1s0.4-1,1-1h15c0.6,0,1,0.4,1,1s-0.4,1-1,1H11z"></path>
            </svg>
          </span>
        </div>
        <div className="message-body">
          {messages.map((message) => (
            <div key={message.id} className={`message-item ${message.sender}`}>
              <p>{message.text}</p>
            </div>
          ))}
        </div>
        <div className="message-footer">
          <form onSubmit={sendMessage} className="input-container">
            <textarea
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              cols="30"
              rows="1"
              placeholder="Viết tin nhắn"
              className="message-input"
            ></textarea>
            
          </form>
          <button type="submit" className="send-button">
            <svg 
                xmlns="http://www.w3.org/2000/svg" 
                viewBox="0 0 2 24" 
                width="16" 
                height="16" 
                fill="#888888" // Bạn có thể thay đổi màu sắc ở đây
            >
                <path d="M2 12L22 2L15 22L8 17L2 12Z" />
            </svg>
            </button>


        </div>
      </div>

    
    </div>
  );
}
