import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HospitalInterface = () => {
  const [doctors, setDoctors] = useState([]);
  const [educationLevels, setEducationLevels] = useState([]);
  const [searchQuery, setSearchQuery] = useState({
    name: '',
    specialty: '',
    gender: '',
    education: '',
  });

  // Load dữ liệu bác sĩ
  const loadDoctors = async () => {
    const response = await axios.get("http://localhost:5000/api/getallbs");
    setDoctors(response.data);
  };

  // Load dữ liệu trình độ
  const loadEducationLevels = async () => {
    const response = await axios.get("http://localhost:5000/api/getalltrinhdo");
    setEducationLevels(response.data);
  };

  // Gọi API khi component được mount
  useEffect(() => {
    loadDoctors();
    loadEducationLevels();
  }, []);

  // Xử lý tìm kiếm
  const handleSearch = () => {
    // Đây chỉ là ví dụ lọc đơn giản phía client.
    // Thực tế có thể gọi API với các điều kiện tìm kiếm để trả về kết quả phù hợp.
    const filteredDoctors = doctors.filter((doctor) => {
      return (
        (searchQuery.name === '' || doctor.ho_ten.toLowerCase().includes(searchQuery.name.toLowerCase())) &&
        (searchQuery.specialty === '' || doctor.chuyen_khoa === searchQuery.specialty) &&
        (searchQuery.gender === '' || doctor.gioi_tinh === searchQuery.gender) &&
        (searchQuery.education === '' || doctor.trinh_do === searchQuery.education)
      );
    });
    setDoctors(filteredDoctors);
  };

  return (
    <div className="container-bs">
      <div className="header-bs">
        <p>
          Phòng khám Đa khoa phố nối còn là nơi hội tụ của hơn 15 tiến sĩ, chuyên gia đầu ngành trong mọi lĩnh vực, 
          phần lớn các chuyên gia đều đã và đang làm việc tại các bệnh viện lớn như Bệnh viện Bạch Mai, 
          Bệnh viện 108, Bệnh viện 103, Bệnh viện Việt Đức, Bệnh viện Phụ sản Trung ương, 
          Bệnh viện Nhi Trung ương, Bệnh viện Tai – Mũi – Họng Trung ương… và luôn hành nghề theo tiêu chí:
        </p>
        <blockquote>“Khám với chuyên gia – Tận tâm như người nhà”</blockquote>
      </div>

      <div className="search-section">
        <input
          type="text"
          placeholder="Tìm theo tên bác sĩ"
          className="search-input"
          value={searchQuery.name}
          onChange={(e) => setSearchQuery({ ...searchQuery, name: e.target.value })}
        />
        <select
          className="specialty-select"
          onChange={(e) => setSearchQuery({ ...searchQuery, specialty: e.target.value })}
        >
          <option value="">Chuyên khoa</option>
          <option value="Chuyên khoa 1">Chuyên khoa 1</option>
          <option value="Chuyên khoa 2">Chuyên khoa 2</option>
        </select>
        <select
          className="gender-select"
          onChange={(e) => setSearchQuery({ ...searchQuery, gender: e.target.value })}
        >
          <option value="">Giới tính</option>
          <option value="Nam">Nam</option>
          <option value="Nữ">Nữ</option>
        </select>
        <select
          className="education-select"
          onChange={(e) => setSearchQuery({ ...searchQuery, education: e.target.value })}
        >
          <option value="">Học vấn</option>
          {educationLevels.map((level) => (
            <option key={level.id_trinh_do} value={level.ten_trinh_do}>
              {level.ten_trinh_do}
            </option>
          ))}
        </select>
        <button className="search-button" onClick={handleSearch}>
          Tìm kiếm
        </button>
      </div>

      <div className="doctors-section">
        {doctors.map((doctor) => (
          <div key={doctor.id_bac_si} className="doctor-card">
            <img src={doctor.hinh_anh_bs} alt="Bác sĩ" className="doctor-image" />
            <p>Bác sĩ</p>
            <h3>{doctor.ho_ten}</h3>
            <p>{doctor.trinh_do} – {doctor.chuc_danh}</p>
            <p>{doctor.mo_ta}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HospitalInterface;
