import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function RegistrationForm() {
  const [formData, setFormData] = useState({
    phone: '',
    dateOfBirth: '',
    fullName: '',
    customerID: '',
    gender: 'Nam',
    nationality: 'VIET NAM (Việt Nam)',
    city: '',
    district: '',
    ward: '',
    address: '',
    selectedDoctor: '',
    selectedSpecialty: '',
    appointmentDate: '',
    healthCondition: '',
  });

  const navigate = useNavigate(); // Sử dụng useNavigate thay cho useHistory

  // Lấy dữ liệu appointment từ localStorage khi component được mount
  useEffect(() => {
    const appointmentData = JSON.parse(localStorage.getItem('appointmentData'));
    if (appointmentData) {
      setFormData(prevData => ({
        ...prevData,
        selectedDoctor: appointmentData.selectedDoctor,
        selectedSpecialty: appointmentData.selectedSpecialty,
        appointmentDate: appointmentData.appointmentDate,
        healthCondition: appointmentData.healthCondition,
      }));
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const appointmentData = JSON.parse(localStorage.getItem('appointmentData')); // Retrieve appointment data

    const completeData = {
      ...appointmentData,
      ...formData,
    };

    // Log data to console to check if all required fields are filled
    console.log('Complete Data:', completeData);

    const requiredFields = ['phone', 'dateOfBirth', 'fullName', 'selectedDoctor', 'selectedSpecialty'];
    const missingFields = requiredFields.filter(field => !completeData[field]);

    if (missingFields.length > 0) {
      console.log('Missing fields:', missingFields);
      alert(`Vui lòng điền đầy đủ thông tin: ${missingFields.join(', ')}`);
      return; // Do not proceed if there are missing fields
    }

    try {
      const response = await fetch('http://localhost:5000/api/addAppointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(completeData),
      });

      if (!response.ok) {
        throw new Error('Failed to create appointment');
      }

      alert('Đặt lịch khám thành công!');
      localStorage.removeItem('appointmentData'); // Clear appointment data from localStorage
      navigate('/success'); // Navigate to success page or other page as needed
    } catch (error) {
      console.error('Error:', error);
      alert('Có lỗi xảy ra, vui lòng thử lại sau.');
    }
  };

  return (
    <div className="registration-form-container">
      <h2>ĐĂNG KÝ KHÁM BỆNH</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Họ tên:</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Ngày sinh:</label>
          <input
            type="date"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Số điện thoại:</label>
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>CMND/CCCD:</label>
          <input
            type="text"
            name="customerID"
            value={formData.customerID}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Giới tính:</label>
          <select
            name="gender"
            value={formData.gender}
            onChange={handleChange}
          >
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
          </select>
        </div>
        <div>
          <label>Quốc tịch:</label>
          <input
            type="text"
            name="nationality"
            value={formData.nationality}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Tỉnh/Thành phố:</label>
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Quận/Huyện:</label>
          <input
            type="text"
            name="district"
            value={formData.district}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Xã/Phường:</label>
          <input
            type="text"
            name="ward"
            value={formData.ward}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Địa chỉ:</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Bác sĩ đã chọn:</label>
          <input
            type="text"
            name="selectedDoctor"
            value={formData.selectedDoctor}
            readOnly
          />
        </div>
        <div>
          <label>Chuyên khoa đã chọn:</label>
          <input
            type="text"
            name="selectedSpecialty"
            value={formData.selectedSpecialty}
            readOnly
          />
        </div>
        <div>
          <label>Ngày khám:</label>
          <input
            type="text"
            name="appointmentDate"
            value={formData.appointmentDate}
            readOnly
          />
        </div>
        <div>
          <label>Tình trạng sức khỏe:</label>
          <textarea
            name="healthCondition"
            value={formData.healthCondition}
            readOnly
          />
        </div>
        <div>
          <button type="submit">ĐĂNG KÝ</button>
        </div>
      </form>
    </div>
  );
}

export default RegistrationForm;
