import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function AppointmentForm() {
  const [departments, setDepartments] = useState([]); // State cho danh sách chuyên khoa
  const [doctors, setDoctors] = useState([]); // State cho danh sách bác sĩ dựa trên chuyên khoa đã chọn
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [healthCondition, setHealthCondition] = useState('');

  // Lấy danh sách chuyên khoa từ API khi component được mount
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getalldm');
        if (!response.ok) {
          throw new Error('Failed to fetch departments');
        }
        const data = await response.json();
        setDepartments(data); // Cập nhật state với dữ liệu đã lấy
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };
    fetchDepartments();
  }, []);

  // Lấy danh sách bác sĩ dựa trên chuyên khoa đã chọn
  useEffect(() => {
    if (selectedSpecialty) {
      const fetchDoctors = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/getdoctors/${selectedSpecialty}`);
          if (!response.ok) {
            throw new Error('Failed to fetch doctors');
          }
          const data = await response.json();
          setDoctors(data); // Cập nhật danh sách bác sĩ
        } catch (error) {
          console.error('Error fetching doctors:', error);
        }
      };
      fetchDoctors();
    } else {
      setDoctors([]); // Reset danh sách bác sĩ nếu không có chuyên khoa nào được chọn
    }
  }, [selectedSpecialty]);

  // Hàm xử lý khi gửi form
  const handleSubmit = async (e) => {
    e.preventDefault(); // Ngăn chặn hành động mặc định của form
    const formData = {
      selectedDoctor,
      selectedSpecialty,
      appointmentDate,
      healthCondition,
    };
    
    console.log('Form Data Submitted:', formData); // In dữ liệu form ra console

    try {
      const response = await fetch('http://localhost:5000/api/addAppointment', {
        method: 'POST', // Phương thức gửi dữ liệu
        headers: {
          'Content-Type': 'application/json', // Định dạng dữ liệu gửi lên
        },
        body: JSON.stringify(formData), // Chuyển đổi dữ liệu thành chuỗi JSON
      });

      if (!response.ok) {
        throw new Error('Failed to create appointment'); // Xử lý lỗi nếu không thành công
      }

      const result = await response.json(); // Nhận phản hồi từ API
      console.log('Appointment created:', result); // In phản hồi ra console
      // Có thể thêm thông báo cho người dùng khi tạo lịch thành công ở đây

    } catch (error) {
      console.error('Error submitting appointment:', error); // Xử lý lỗi
      // Có thể thêm thông báo cho người dùng nếu có lỗi
    }
  };

  return (
    <div className="form-container-dl">
      <h2>ĐĂNG KÝ KHÁM BỆNH</h2>
      <p>Quý khách hàng có nhu cầu đặt hẹn khám tại Hệ thống phòng khám đa khoa Phố Nối</p>

      <form onSubmit={handleSubmit} className="appointment-form-dl">
        <div className="form-group-dl">
          <label>Chọn loại dịch vụ khám</label>
          <div className="radio-group-dl">
            <label>
              <input type="radio" name="service" value="Khám trong giờ" /> Khám trong giờ
            </label>
            <label>
              <input type="radio" name="service" value="Khám ngoài giờ" /> Khám ngoài giờ
            </label>
            <label>
              <input type="radio" name="service" value="Khám online" /> Khám online
            </label>
          </div>
        </div>

        <div className="form-group-dl">
          <label>Chọn chuyên khoa</label>
          <select
            value={selectedSpecialty}
            onChange={(e) => setSelectedSpecialty(e.target.value)}
          >
            <option value="">-- Chọn chuyên khoa --</option>
            {departments.map((dept) => (
              <option key={dept.id_khoa} value={dept.id_khoa}>
                {dept.ten_khoa}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label>Chọn bác sĩ</label>
          <select
            value={selectedDoctor}
            onChange={(e) => setSelectedDoctor(e.target.value)}
          >
            <option value="">-- Chọn bác sĩ --</option>
            {doctors.map((doctor) => (
              <option key={doctor.id_bac_si} value={doctor.ho_ten}>
                {doctor.ho_ten} ({doctor.chuc_danh})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group-dl">
          <label>Chọn ngày - khung giờ muốn khám</label>
          <input
            type="date"
            value={appointmentDate}
            onChange={(e) => setAppointmentDate(e.target.value)}
          />
        </div>

        <div className="form-group-dl">
          <label>Nhập vấn đề sức khỏe cần khám</label>
          <textarea
            placeholder="Nhập tình trạng sức khỏe của bạn"
            value={healthCondition}
            onChange={(e) => setHealthCondition(e.target.value)}
          ></textarea>
        </div>

        <div className="button-group-dl">
          <button type="submit" className="submit-btn-dl">TIẾP THEO</button>
        </div>
      </form>
    </div>
  );
}

export default AppointmentForm;
