import React from 'react';
import axios from 'axios';
import  { useEffect, useState } from 'react'

const HospitalInterface = () => {
    const[data,setData] = useState([]);

    const loadData = async() =>{
        const response = await axios.get("http://localhost:5000/api/getallnv");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);
    return (
        <div class="container-bs">
        <div class="header-bs">
            <p>
                Bệnh viện Đa khoa Hà Nội còn là nơi hội tụ của hơn 60 giáo sư, chuyên gia đầu ngành trong mọi lĩnh vực, 
                phần lớn các chuyên gia đều đã và đang làm việc tại các bệnh viện lớn như Bệnh viện Bạch Mai, 
                Bệnh viện 108, Bệnh viện 103, Bệnh viện Việt Đức, Bệnh viện Phụ sản Trung ương, 
                Bệnh viện Nhi Trung ương, Bệnh viện Tai – Mũi – Họng Trung ương… và luôn hành nghề theo tiêu chí:
            </p>
            <blockquote>“Khám với chuyên gia – Tận tâm như người nhà”</blockquote>
        </div>

        <div class="search-section">
            <input type="text" placeholder="Tìm theo tên bác sĩ" class="search-input"/>
            <select class="specialty-select">
                <option>Chuyên khoa</option>
                <option>Chuyên khoa 1</option>
                <option>Chuyên khoa 2</option>
            </select>
            <select class="gender-select">
                <option>Giới tính</option>
                <option>Nam</option>
                <option>Nữ</option>
            </select>
            <select class="education-select">
                <option>Học vấn</option>
                <option>Cao đẳng</option>
                <option>Đại học</option>
                <option>Thạc sĩ</option>
                <option>Tiến sĩ</option>
            </select>
            <button class="search-button">Tìm kiếm</button>
        </div>

        <div class="doctors-section">
            {data.map((item,index)=>{
                return( 
                     <div key={item.id_ba} class="doctor-card">
                    <img src="/images/Bacsi5.jpg" alt="BS NGUYỄN THỊ THÙY" class="doctor-image"/>
                    <p>Bác sĩ</p>
                    <h3>BS NGUYỄN THỊ THÙY</h3>
                    <p>BS Nguyễn Thị Thùy – Trưởng khoa Xét nghiệm Bệnh viện Đa khoa Hà Nội – Bác sĩ xét nghiệm Bệnh viện Đa khoa...</p>
                </div>)
               
            })}
            
        </div>
    </div>
    );
};

export default HospitalInterface;
