import React, { useState } from "react";

export default function Chonsize() {
  const [gender, setGender] = useState(""); // Lưu giới tính
  const [height, setHeight] = useState(170); // Chiều cao mặc định
  const [weight, setWeight] = useState(57); // Cân nặng mặc định
  const [suggestion, setSuggestion] = useState("Vui lòng nhập thông tin"); // Gợi ý

  // Hàm tính trạng thái cân nặng
  const calculateSuggestion = (height, weight, gender) => {
    if (!gender) return "Vui lòng chọn giới tính";

    const bmi = weight / ((height / 100) * (height / 100)); // Tính BMI
    if (bmi < 18.5) return "Cân nặng hơi thấp";
    if (bmi >= 18.5 && bmi <= 24.9) return "Cân nặng ổn định";
    if (bmi >= 25 && bmi <= 29.9) return "Cân nặng hơi cao";
    return "Cân nặng quá cao";
  };

  // Xử lý thay đổi giới tính
  const handleGenderChange = (e) => {
    const selectedGender = e.target.value;
    setGender(selectedGender);
    setSuggestion(calculateSuggestion(height, weight, selectedGender));
  };

  // Xử lý thay đổi chiều cao
  const handleHeightChange = (e) => {
    const newHeight = parseInt(e.target.value, 10);
    setHeight(newHeight);
    setSuggestion(calculateSuggestion(newHeight, weight, gender));
  };

  // Xử lý thay đổi cân nặng
  const handleWeightChange = (e) => {
    const newWeight = parseInt(e.target.value, 10);
    setWeight(newWeight);
    setSuggestion(calculateSuggestion(height, newWeight, gender));
  };

  return (
    <div style={{ width: "1280px", margin: "150px auto 0px" }}>
      <h2 style={{ textAlign: "center", margin: "40px 0", fontWeight: "500" }}>
        Tư vấn cân nặng
      </h2>
      <div className="step">
        <span>1</span>
        <span>2</span>
        <span>3</span>
      </div>
      <div className="row">
        <div className="col p-4">
          <p
            style={{
              textAlign: "center",
              fontWeight: "700",
              fontSize: "14px",
              marginBottom: "20px",
            }}
          >
            Giới Tính
          </p>
          <div className="size-left" style={{ position: "sticky", top: "10px" }}>
            <div className="choose-product">
              <select
                style={{ padding: "10px", width: "100%" }}
                value={gender}
                onChange={handleGenderChange}
              >
                <option value="">-- Chọn giới tính --</option>
                <option value="Nam">Nam</option>
                <option value="Nữ">Nữ</option>
              </select>
            </div>
          </div>
        </div>
        <div className="col p-8">
          <div
            className="row"
            style={{
              borderBottom: "1px solid #d9d9d9",
              paddingBottom: "35px",
            }}
          >
            <div className="col p-6">
              <p
                style={{
                  textAlign: "center",
                  fontWeight: "700",
                  fontSize: "14px",
                  marginBottom: "20px",
                }}
              >
                Thông số cơ thể
              </p>
              <div className="size-center">
                <div style={{ marginTop: "20px" }}>
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      fontWeight: "700",
                      marginBottom: "10px",
                    }}
                  >
                    <span>Chiều cao</span>
                    <span className="cm">{height} cm</span>
                  </div>
                  <input
                    type="range"
                    name="c-height"
                    step="1"
                    min="155"
                    max="183"
                    value={height}
                    onChange={handleHeightChange}
                    id="input-cm"
                  />
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      fontWeight: "700",
                      marginBottom: "10px",
                    }}
                  >
                    <span>Cân nặng</span>
                    <span className="kg">{weight} kg</span>
                  </div>
                  <input
                    type="range"
                    name="c-weight"
                    step="1"
                    min="48"
                    max="84"
                    value={weight}
                    onChange={handleWeightChange}
                    id="input-kg"
                  />
                </div>
              </div>
            </div>
            <div className="col p-6">
              <p
                style={{
                  textAlign: "center",
                  fontWeight: "700",
                  fontSize: "14px",
                  marginBottom: "20px",
                }}
              >
                Phòng khám gợi ý
              </p>
              <div
                className="size-result"
                style={{
                  textAlign: "center",
                  padding: "10px",
                  backgroundColor: "#f8f9fa",
                  borderRadius: "5px",
                  fontWeight: "700",
                }}
              >
                {suggestion}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
