import React, { useState, useEffect } from 'react';
import RegistrationForm from './RegistrationForm';

function AppointmentPage() {
  const [registrationData, setRegistrationData] = useState(null);
  const [doctors, setDoctors] = useState([]); // Danh sách bác sĩ
  const [selectedDoctor, setSelectedDoctor] = useState('');
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [healthCondition, setHealthCondition] = useState('');

  // Lấy danh sách khoa từ API
  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getalldm');
        if (!response.ok) throw new Error('Failed to fetch departments');
        const data = await response.json();
        setDepartments(data); // Cập nhật danh sách khoa
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };
    fetchDepartments();
  }, []);

  // Lấy danh sách bác sĩ dựa trên khoa được chọn
  useEffect(() => {
    const fetchDoctors = async () => {
      if (selectedSpecialty) {
        try {
          const response = await fetch(`http://localhost:5000/api/getdoctors/${selectedSpecialty}`);
          if (!response.ok) throw new Error('Failed to fetch doctors');
          const data = await response.json();
          setDoctors(data); // Cập nhật danh sách bác sĩ
        } catch (error) {
          console.error('Error fetching doctors:', error);
        }
      } else {
        setDoctors([]); // Reset danh sách bác sĩ nếu không có khoa nào được chọn
      }
    };
    fetchDoctors();
  }, [selectedSpecialty]);

  const handleRegistrationSubmit = (data) => {
    setRegistrationData(data); // Lưu trữ dữ liệu đăng ký
  };

  const handleAppointmentSubmit = async (e) => {
    e.preventDefault();
    const formData = {
      ...registrationData,
      selectedDoctor,
      selectedSpecialty,
      appointmentDate,
      healthCondition,
    };
    
    try {
      const response = await fetch('http://localhost:5000/api/addAppointment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to book appointment');
      }

      const result = await response.json();
      alert('Đặt lịch thành công!'); // Thông báo thành công
      console.log('Appointment booked successfully:', result);
    } catch (error) {
      console.error('Error booking appointment:', error);
      alert('Đặt lịch không thành công! Vui lòng thử lại.');
    }
  };

  return (
    <div>
      <h1>Đặt Lịch Khám</h1>
      <RegistrationForm onSubmit={handleRegistrationSubmit} />
      <div className="form-container-dl">
        <h2>ĐĂNG KÝ KHÁM BỆNH</h2>
        <form onSubmit={handleAppointmentSubmit} className="appointment-form-dl">
          {/* Các trường nhập dữ liệu */}
          <div className="form-group-dl">
            <label>Chọn chuyên khoa</label>
            <select value={selectedSpecialty} onChange={(e) => setSelectedSpecialty(e.target.value)}>
              <option value="">-- Chọn chuyên khoa --</option>
              {departments.map((dept) => (
                <option key={dept.id} value={dept.id}>
                  {dept.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group-dl">
            <label>Chọn bác sĩ</label>
            <select value={selectedDoctor} onChange={(e) => setSelectedDoctor(e.target.value)} disabled={!selectedSpecialty}>
              <option value="">-- Chọn bác sĩ --</option>
              {doctors.map((doc) => (
                <option key={doc.id} value={doc.id}>
                  {doc.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group-dl">
            <label>Ngày hẹn</label>
            <input type="date" value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} required />
          </div>

          <div className="form-group-dl">
            <label>Triệu chứng sức khỏe</label>
            <textarea value={healthCondition} onChange={(e) => setHealthCondition(e.target.value)} />
          </div>

          <div className="button-group-dl">
            <button type="submit" className="submit-btn-dl">ĐẶT HẸN</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default AppointmentPage;
