export default function Message() {
  return (
    <div>
      <div className="message">
        <div className="message-header">
          <span>
            <svg color="#111111" viewBox="0 0 32 32" aria-hidden="true" className="lc-1mpchac">
              <path d="M8,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S9.1,18,8,18z M16,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S17.1,18,16,18z M24,18c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S25.1,18,24,18z"></path>
            </svg>
          </span>
          <h3>Chat Cùng Bác Sĩ</h3>
          <span className="hide-message">
            <svg color="inherit" viewBox="0 0 32 32" aria-hidden="true" className="lc-1mpchac">
              <path d="M11,22c-0.6,0-1-0.4-1-1s0.4-1,1-1h15c0.6,0,1,0.4,1,1s-0.4,1-1,1H11z"></path>
            </svg>
          </span>
        </div>
        <div className="message-body">
          {/* Messages will go here */}
          <div className="message-bubble patient">Chào bác sĩ, tôi có một số câu hỏi về sức khỏe.</div>
          <div className="message-bubble doctor">Chào bạn! Bạn có thể cho tôi biết vấn đề cụ thể của bạn không?</div>
          <div className="message-bubble patient">Tôi cảm thấy đau đầu và chóng mặt.</div>
          <div className="message-bubble doctor">Bạn có gặp phải triệu chứng nào khác không?</div>
          <div className="message-bubble patient">Tôi cảm thấy hơi mệt.</div>
        </div>
        <div className="message-footer">
          <textarea
            className="message-input"
            placeholder="Viết tin nhắn"
          ></textarea>
          <button className="send-button">
            <svg viewBox="0 0 24 24" className="send-icon">
              <path d="M2 12l9 4 7-7-4 9L22 2l-20 10h10z"></path>
            </svg>
          </button>
        </div>
      </div>

      <style jsx>{`
        .message {
          display: flex;
          flex-direction: column;
          border: 1px solid #ccc;
          border-radius: 8px;
          padding: 16px;
          width: 400px; /* Adjust width as needed */
          margin: 20px auto; /* Center the message box */
        }
        .message-header {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .message-body {
          flex-grow: 1;
          margin: 10px 0;
          overflow-y: auto; /* Allow scrolling for long messages */
          max-height: 200px; /* Set max height for the messages area */
        }
        .message-bubble {
          border-radius: 8px;
          padding: 10px;
          margin: 5px 0;
          width: fit-content; /* Adjust width to fit content */
          max-width: 80%; /* Limit max width */
        }
        .patient {
          background-color: #e0f7fa; /* Light blue for patient */
          align-self: flex-start; /* Align left */
        }
        .doctor {
          background-color: #ffccbc; /* Light pink for doctor */
          align-self: flex-end; /* Align right */
        }
        .message-footer {
          display: flex;
          align-items: center;
        }
        .message-input {
          flex-grow: 1;
          border: 1px solid #ccc;
          border-radius: 4px;
          padding: 5px; /* Smaller padding */
          margin-right: 10px;
          resize: none; /* Prevent resizing */
          height: 30px; /* Fixed height for input */
          font-size: 14px; /* Adjust font size */
        }
        .send-button {
          background-color: blue;
          color: white;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          padding: 5px 10px; /* Smaller padding for button */
        }
        .send-icon {
          fill: white; /* Change icon color */
          width: 20px;
          height: 20px;
        }
      `}</style>
    </div>
  );
}
